import React, { useState, useEffect } from "react";
import { schemes } from "./data";
import "./style.css";

import bgDefault from "./assets/backgrounds/bg-default.jpg";
import defaultImg from "./assets/schemes/default.jpg";

/* IMAGE MAP */
import tsPost from "./assets/schemes/ts-postmatric.jpg";
import nspImg from "./assets/schemes/nsp.jpg";
import aicteImg from "./assets/schemes/aicte.jpg";
import pmkisanImg from "./assets/schemes/pmkisan.jpg";
import rythuImg from "./assets/schemes/rythubharosa.jpg";
import aasaraImg from "./assets/schemes/aasara.jpg";
import ayushmanImg from "./assets/schemes/ayushman.jpg";
import mahalaxmiImg from "./assets/schemes/mahalaxmi.jpg";
import gruhaJyothiImg from "./assets/schemes/gruha-jyothi.jpg";

const schemeImages = {
  "ts-postmatric": tsPost,
  nsp: nspImg,
  aicte: aicteImg,
  pmkisan: pmkisanImg,
  rythubharosa: rythuImg,
  aasara: aasaraImg,
  ayushman: ayushmanImg,
  mahalaxmi: mahalaxmiImg,
  "gruha-jyothi": gruhaJyothiImg
};

const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;

/* TEXT + QUESTIONS assumed already defined above */

export default function App() {
  /* ---------------- STATES ---------------- */
  const [step, setStep] = useState(0);
  const [qStep, setQStep] = useState(0);
  const [lang, setLang] = useState("en");

  const [user, setUser] = useState({
    name: "",
    gender: "",
    age: "",
    occupation: "",
    area: "",
    income: "",
    disability: ""
  });

  const [isSpeaking, setIsSpeaking] = useState(false);
  const [voiceIndex, setVoiceIndex] = useState(0);

  /* ---------------- VOICE FUNCTIONS ---------------- */
  const speakText = (text) => {
    speechSynthesis.cancel();
    const u = new SpeechSynthesisUtterance(text);
    u.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    u.rate = 0.9;
    speechSynthesis.speak(u);
  };

  const startVoiceForSchemes = () => {
    if (eligible.length === 0) {
      speakText("No eligible schemes found");
      return;
    }
    setVoiceIndex(0);
    setIsSpeaking(true);
  };

  const stopVoice = () => {
    speechSynthesis.cancel();
    setIsSpeaking(false);
    setVoiceIndex(0);
  };

  const startListening = () => {
    if (!SpeechRecognition) return;
    const rec = new SpeechRecognition();
    rec.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    rec.start();

    rec.onresult = (e) => {
      const text = e.results[0][0].transcript.toLowerCase();
      const q = QUESTIONS[qStep];

      if (q.key === "gender") {
        if (text.includes("male")) setUser({ ...user, gender: "male" });
        if (text.includes("female")) setUser({ ...user, gender: "female" });
      }
    };
  };

  /* ---------------- ELIGIBILITY ---------------- */
  const eligible = schemes.filter((s) => {
    const occ = s.occupation.includes("any") || s.occupation.includes(user.occupation);
    const inc = s.income.includes("any") || s.income.includes(user.income);
    const dis = s.disability.includes("any") || s.disability.includes(user.disability);
    return occ && inc && dis;
  });

  useEffect(() => {
    if (!isSpeaking || voiceIndex >= eligible.length) return;

    const s = eligible[voiceIndex];
    const u = new SpeechSynthesisUtterance(
      `${s.name[lang]}. ${s.benefit[lang]}`
    );
