export default function App() {
  const [step, setStep] = useState(0);
  const [qIndex, setQIndex] = useState(0);
  const [lang, setLang] = useState("en");
  const [activeScheme, setActiveScheme] = useState(null);

  const [user, setUser] = useState({
    gender: "",
    ageGroup: "",
    occupation: "",
    income: "",
    area: "",
    disability: ""
  });

  /* ---------- SPEAK ---------- */
  const speak = (text, onEnd) => {
    window.speechSynthesis.cancel();
    const u = new SpeechSynthesisUtterance(text);
    u.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    u.rate = 0.9;
    if (onEnd) u.onend = onEnd;
    window.speechSynthesis.speak(u);
  };

  /* ---------- AUTO QUESTION VOICE ---------- */
  useEffect(() => {
    if (step === 1) speak(QUESTIONS[qIndex][lang]);
  }, [step, qIndex, lang]);

  /* ---------- VOICE INPUT ---------- */
  const startListening = () => {
    if (!SR) return alert("Speech not supported");
    const rec = new SR();
    rec.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    rec.start();
    rec.onresult = (e) => {
      setUser({ ...user, [QUESTIONS[qIndex].key]: e.results[0][0].transcript.toLowerCase() });
    };
  };

  /* ---------- STRICT ELIGIBILITY ---------- */
  const eligibleSchemes = schemes.filter((s) => {
    const e = s.eligibility;
    return (
      (e.gender.includes("any") || e.gender.includes(user.gender)) &&
      (e.ageGroup.includes("any") || e.ageGroup.includes(user.ageGroup)) &&
      (e.occupation.includes("any") || e.occupation.includes(user.occupation)) &&
      (e.income.includes("any") || e.income.includes(user.income)) &&
      (e.area.includes("any") || e.area.includes(user.area)) &&
      (e.disability.includes("any") || e.disability.includes(user.disability))
    );
  });

  /* ---------- AUTO ANNOUNCE ELIGIBLE ---------- */
  useEffect(() => {
    if (step === 2 && eligibleSchemes.length > 0) {
      speak(
        lang === "te"
          ? "‡∞Æ‡±Ä ‡∞Ö‡∞∞‡±ç‡∞π‡∞§ ‡∞Ü‡∞ß‡∞æ‡∞∞‡∞Ç‡∞ó‡∞æ ‡∞à ‡∞™‡∞•‡∞ï‡∞æ‡∞≤‡±Å ‡∞Ö‡∞Ç‡∞¶‡±Å‡∞¨‡∞æ‡∞ü‡±Å‡∞≤‡±ã ‡∞â‡∞®‡±ç‡∞®‡∞æ‡∞Ø‡∞ø"
          : lang === "hi"
          ? "‡§Ü‡§™‡§ï‡•Ä ‡§™‡§æ‡§§‡•ç‡§∞‡§§‡§æ ‡§ï‡•á ‡§Ü‡§ß‡§æ‡§∞ ‡§™‡§∞ ‡§Ø‡•á ‡§Ø‡•ã‡§ú‡§®‡§æ‡§è‡§Å ‡§â‡§™‡§≤‡§¨‡•ç‡§ß ‡§π‡•à‡§Ç"
          : "Based on your eligibility, these schemes are available"
      );
    }
  }, [step, lang]);

  /* ---------- EXPLAIN ONE BY ONE ---------- */
  const explainScheme = (index) => {
    const s = eligibleSchemes[index];
    setActiveScheme(s.id);

    speak(
      lang === "te"
        ? `${s.name.te}. ${s.benefit.te}`
        : lang === "hi"
        ? `${s.name.hi}. ${s.benefit.hi}`
        : `${s.name.en}. ${s.benefit.en}`,
      () => setActiveScheme(null)
    );
  };

  /* ---------- HOME ---------- */
  const goHome = () => {
    window.speechSynthesis.cancel();
    setUser({ gender:"", ageGroup:"", occupation:"", income:"", area:"", disability:"" });
    setQIndex(0);
    setStep(0);
  };

  /* ---------- HOME PAGE ---------- */
  if (step === 0) {
    return (
      <div className="page center">
        <div className="card center">
          <h1>{TEXT.title[lang]}</h1>
          <select value={lang} onChange={(e) => setLang(e.target.value)}>
            <option value="en">English</option>
            <option value="te">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            <option value="hi">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
          </select>
          <button className="primary" onClick={() => setStep(1)}>
            {TEXT.start[lang]}
          </button>
        </div>
      </div>
    );
  }

  /* ---------- QUESTIONS ---------- */
  if (step === 1) {
    const q = QUESTIONS[qIndex];
    return (
      <div className="page center">
        <div className="card center">
          <h2>{q[lang]}</h2>
          <select value={user[q.key]} onChange={(e) => setUser({ ...user, [q.key]: e.target.value })}>
            <option value="">Select</option>
            {q.key === "occupation" && (
              <>
                <option value="student">Student</option>
                <option value="farmer">Farmer</option>
                <option value="homemaker">Homemaker</option>
                <option value="elderly">Elderly</option>
                <option value="unemployed">Unemployed</option>
              </>
            )}
          </select>
          <button onClick={startListening}>üé§ {TEXT.speak[lang]}</button>
          <div className="nav">
            <button onClick={() => qIndex === 0 ? setStep(0) : setQIndex(qIndex - 1)}>{TEXT.back[lang]}</button>
            <button className="primary" onClick={() => qIndex < QUESTIONS.length - 1 ? setQIndex(qIndex + 1) : setStep(2)}>{TEXT.next[lang]}</button>
          </div>
        </div>
      </div>
    );
  }

  /* ---------- RESULTS ---------- */
  return (
    <div className="results-page">
      <div className="results-header">
        <h2>{TEXT.results[lang]}</h2>
        <button className="home-btn" onClick={goHome}>üè† Home</button>
      </div>

      <div className="scheme-row">
        {eligibleSchemes.map((s, i) => (
          <div className={`scheme-card ${activeScheme === s.id ? "active" : ""}`} key={s.id}>
            <img src={schemeImages[s.image] || defaultImg} alt={s.name[lang]} />
            <h3>{s.name[lang]}</h3>
            <p>{s.benefit[lang]}</p>
            <button className="explain-btn" onClick={() => explainScheme(i)}>üîä Explain</button>
            <a href={s.officialLink} target="_blank" rel="noreferrer">Official Website</a>
          </div>
        ))}
      </div>
    </div>
  );
}
