import React, { useState, useEffect } from "react";
import { schemes } from "./data";

// background
import bgDefault from "./assets/backgrounds/bg-default.jpg";

// scheme images
import tsPost from "./assets/schemes/ts-postmatric.jpg";
import nspImg from "./assets/schemes/nsp.jpg";
import aicteImg from "./assets/schemes/aicte.jpg";
import pmkisanImg from "./assets/schemes/pmkisan.jpg";
import rythuImg from "./assets/schemes/rythubharosa.jpg";
import aasaraImg from "./assets/schemes/aasara.jpg";
import ayushmanImg from "./assets/schemes/ayushman.jpg";
import defaultImg from "./assets/schemes/default.jpg";

const schemeImages = {
  "ts-postmatric": tsPost,
  nsp: nspImg,
  aicte: aicteImg,
  pmkisan: pmkisanImg,
  rythubharosa: rythuImg,
  aasara: aasaraImg,
  ayushman: ayushmanImg
};

const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;

const TEXT = {
  title: {
    en: "Welfare Voice Assistant",
    te: "‡∞∏‡∞Ç‡∞ï‡±ç‡∞∑‡±á‡∞Æ ‡∞µ‡∞æ‡∞Ø‡∞ø‡∞∏‡±ç ‡∞Ö‡∞∏‡∞ø‡∞∏‡±ç‡∞ü‡±Ü‡∞Ç‡∞ü‡±ç",
    hi: "‡§ï‡§≤‡•ç‡§Ø‡§æ‡§£ ‡§µ‡•â‡§á‡§∏ ‡§∏‡§π‡§æ‡§Ø‡§ï"
  },
  start: { en: "Start", te: "‡∞™‡±ç‡∞∞‡∞æ‡∞∞‡∞Ç‡∞≠‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø", hi: "‡§∂‡•Å‡§∞‡•Ç ‡§ï‡§∞‡•á‡§Ç" },
  next: { en: "Next", te: "‡∞§‡∞∞‡±ç‡∞µ‡∞æ‡∞§", hi: "‡§Ö‡§ó‡§≤‡§æ" },
  back: { en: "Back", te: "‡∞µ‡±Ü‡∞®‡∞ï‡±ç‡∞ï‡∞ø", hi: "‡§µ‡§æ‡§™‡§∏" },
  speak: { en: "Tap to Speak", te: "‡∞Æ‡∞æ‡∞ü‡±ç‡∞≤‡∞æ‡∞°‡∞Ç‡∞°‡∞ø", hi: "‡§¨‡•ã‡§≤‡•á‡§Ç" },
  select: { en: "Select", te: "‡∞é‡∞Ç‡∞ö‡±Å‡∞ï‡±ã‡∞Ç‡∞°‡∞ø", hi: "‡§ö‡•Å‡§®‡•á‡§Ç" },
  results: {
    en: "Eligible Government Schemes",
    te: "‡∞Ö‡∞∞‡±ç‡∞π‡∞Æ‡±à‡∞® ‡∞™‡±ç‡∞∞‡∞≠‡±Å‡∞§‡±ç‡∞µ ‡∞™‡∞•‡∞ï‡∞æ‡∞≤‡±Å",
    hi: "‡§™‡§æ‡§§‡•ç‡§∞ ‡§∏‡§∞‡§ï‡§æ‡§∞‡•Ä ‡§Ø‡•ã‡§ú‡§®‡§æ‡§è‡§Å"
  }
};

const QUESTIONS = [
  { key: "gender", en: "What is your gender?", te: "‡∞Æ‡±Ä ‡∞≤‡∞ø‡∞Ç‡∞ó‡∞Ç ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§≤‡§ø‡§Ç‡§ó ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "age", en: "What is your age?", te: "‡∞Æ‡±Ä ‡∞µ‡∞Ø‡∞∏‡±ç‡∞∏‡±Å ‡∞é‡∞Ç‡∞§?", hi: "‡§Ü‡§™‡§ï‡•Ä ‡§â‡§Æ‡•ç‡§∞ ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "occupation", en: "What is your occupation?", te: "‡∞Æ‡±Ä ‡∞µ‡±É‡∞§‡±ç‡∞§‡∞ø ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§™‡•á‡§∂‡§æ ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "area", en: "Where do you live?", te: "‡∞Æ‡±Ä ‡∞®‡∞ø‡∞µ‡∞æ‡∞∏ ‡∞™‡±ç‡∞∞‡∞æ‡∞Ç‡∞§‡∞Ç ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™ ‡§ï‡§π‡§æ‡§Å ‡§∞‡§π‡§§‡•á ‡§π‡•à‡§Ç?" },
  { key: "income", en: "What is your income range?", te: "‡∞Æ‡±Ä ‡∞Ü‡∞¶‡∞æ‡∞Ø‡∞Ç ‡∞é‡∞Ç‡∞§?", hi: "‡§Ü‡§™‡§ï‡•Ä ‡§Ü‡§Ø ‡§ï‡§ø‡§§‡§®‡•Ä ‡§π‡•à?" },
  { key: "disability", en: "Do you have a disability?", te: "‡∞Æ‡±Ä‡∞ï‡±Å ‡∞µ‡∞ø‡∞ï‡∞≤‡∞æ‡∞Ç‡∞ó‡∞§ ‡∞â‡∞Ç‡∞¶‡∞æ?", hi: "‡§ï‡•ç‡§Ø‡§æ ‡§Ü‡§™‡§ï‡•ã ‡§µ‡§ø‡§ï‡§≤‡§æ‡§Ç‡§ó‡§§‡§æ ‡§π‡•à?" }
];

export default function App() {
  const [step, setStep] = useState(0);
  const [qStep, setQStep] = useState(0);
  const [lang, setLang] = useState("en");

  const [user, setUser] = useState({
    name: "",
    gender: "",
    age: "",
    occupation: "",
    area: "",
    income: "",
    disability: ""
  });

  /* üîä SPEAK */
  


  /* üé§ LISTEN */
  const listen = (field) => {
    if (!SpeechRecognition) return;
    const r = new SpeechRecognition();
    r.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    r.start();
    r.onresult = (e) => {
      setUser({ ...user, [field]: e.results[0][0].transcript.toLowerCase() });
    };
  };

  /* üîä AUTO SPEAK QUESTION */
  useEffect(() => {
    if (step === 1) speak(QUESTIONS[qStep][lang]);
  }, [qStep, step, lang]);

  /* üßÆ ELIGIBILITY */
  const eligible = schemes.filter((s) => {
    const occMatch = !user.occupation || s.occupation.includes(user.occupation);
    const incomeMatch = s.income === "any" || !user.income || s.income === user.income;
    return occMatch && incomeMatch;
  });

  /* üîä READ SCHEMES */
  useEffect(() => {
    if (step === 2 && eligible.length > 0) {
      speechSynthesis.cancel();
      eligible.forEach((s, i) => {
        setTimeout(() => {
          speak(`${s.name[lang]}. ${s.government}. ${s.benefit[lang]}`);
        }, i * 5000);
      });
    }
  }, [step, eligible, lang]);

  /* üè† HOME */
  if (step === 0) {
    return (
      <div className="hero" style={{ backgroundImage: `url(${bgDefault})` }}>
        <div className="card center">
          <h1>{TEXT.title[lang]}</h1>

          <input
            placeholder={
              lang === "te" ? "‡∞Æ‡±Ä ‡∞™‡±á‡∞∞‡±Å ‡∞®‡∞Æ‡±ã‡∞¶‡±Å ‡∞ö‡±á‡∞Ø‡∞Ç‡∞°‡∞ø" :
              lang === "hi" ? "‡§Ö‡§™‡§®‡§æ ‡§®‡§æ‡§Æ ‡§¶‡§∞‡•ç‡§ú ‡§ï‡§∞‡•á‡§Ç" :
              "Enter your name"
            }
            value={user.name}
            onChange={(e) => setUser({ ...user, name: e.target.value })}
          />

          <select value={lang} onChange={(e) => setLang(e.target.value)}>
            <option value="en">English</option>
            <option value="te">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            <option value="hi">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
          </select>

          <button className="primary" onClick={() => setStep(1)}>
            {TEXT.start[lang]}
          </button>
        </div>
      </div>
    );
  }

  /* ‚ùì QUESTIONS (FIXED) */
  {/* STEP INDICATOR */}
<div className="step-indicator">
  <div className="step-text">
    {lang === "te"
      ? `‡∞¶‡∞∂ ${qStep + 1} / ${QUESTIONS.length}`
      : lang === "hi"
      ? `‡§ö‡§∞‡§£ ${qStep + 1} / ${QUESTIONS.length}`
      : `Step ${qStep + 1} of ${QUESTIONS.length}`}
  </div>

  <div className="step-bar">
    <div
      className="step-progress"
      style={{ width: `${((qStep + 1) / QUESTIONS.length) * 100}%` }}
    />
  </div>
</div>

  if (step === 1) {
    const q = QUESTIONS[qStep];

    return (
      <div className="page center">
        <div className="card center">
          <h2>{q[lang]}</h2>

          {/* GENDER */}
          {q.key === "gender" && (
            <select value={user.gender} onChange={(e) => setUser({ ...user, gender: e.target.value })}>
              <option value="">{TEXT.select[lang]}</option>
              <option value="male">{lang === "te" ? "‡∞™‡±Å‡∞∞‡±Å‡∞∑‡±Å‡∞°‡±Å" : lang === "hi" ? "‡§™‡•Å‡§∞‡•Å‡§∑" : "Male"}</option>
              <option value="female">{lang === "te" ? "‡∞∏‡±ç‡∞§‡±ç‡∞∞‡±Ä" : lang === "hi" ? "‡§Æ‡§π‡§ø‡§≤‡§æ" : "Female"}</option>
              <option value="other">{lang === "te" ? "‡∞á‡∞§‡∞∞‡±Å‡∞≤‡±Å" : lang === "hi" ? "‡§Ö‡§®‡•ç‡§Ø" : "Other"}</option>
            </select>
          )}

          {/* AGE */}
          {q.key === "age" && (
            <input
              type="number"
              placeholder={lang === "te" ? "‡∞µ‡∞Ø‡∞∏‡±ç‡∞∏‡±Å ‡∞®‡∞Æ‡±ã‡∞¶‡±Å ‡∞ö‡±á‡∞Ø‡∞Ç‡∞°‡∞ø" : lang === "hi" ? "‡§â‡§Æ‡•ç‡§∞ ‡§¶‡§∞‡•ç‡§ú ‡§ï‡§∞‡•á‡§Ç" : "Enter age"}
              value={user.age}
              onChange={(e) => setUser({ ...user, age: e.target.value })}
            />
          )}

          {/* OCCUPATION */}
          {q.key === "occupation" && (
            <select value={user.occupation} onChange={(e) => setUser({ ...user, occupation: e.target.value })}>
              <option value="">{TEXT.select[lang]}</option>
              <option value="student">{lang === "te" ? "‡∞µ‡∞ø‡∞¶‡±ç‡∞Ø‡∞æ‡∞∞‡±ç‡∞•‡∞ø" : lang === "hi" ? "‡§õ‡§æ‡§§‡•ç‡§∞" : "Student"}</option>
              <option value="farmer">{lang === "te" ? "‡∞∞‡±à‡∞§‡±Å" : lang === "hi" ? "‡§ï‡§ø‡§∏‡§æ‡§®" : "Farmer"}</option>
              <option value="labourer">{lang === "te" ? "‡∞ï‡±Ç‡∞≤‡±Ä" : lang === "hi" ? "‡§Æ‡§ú‡§º‡§¶‡•Ç‡§∞" : "Labourer"}</option>
              <option value="elderly">{lang === "te" ? "‡∞µ‡±É‡∞¶‡±ç‡∞ß‡±Å‡∞°‡±Å" : lang === "hi" ? "‡§µ‡•É‡§¶‡•ç‡§ß" : "Elderly"}</option>
              <option value="poor">{lang === "te" ? "‡∞®‡∞ø‡∞∞‡±Å‡∞¶‡±ç‡∞Ø‡±ã‡∞ó‡∞ø / ‡∞™‡±á‡∞¶" : lang === "hi" ? "‡§¨‡•á‡§∞‡•ã‡§ú‡§º‡§ó‡§æ‡§∞ / ‡§ó‡§∞‡•Ä‡§¨" : "Unemployed / Poor"}</option>
            </select>
          )}

          {/* AREA */}
          {q.key === "area" && (
            <select value={user.area} onChange={(e) => setUser({ ...user, area: e.target.value })}>
              <option value="">{TEXT.select[lang]}</option>
              <option value="rural">{lang === "te" ? "‡∞ó‡±ç‡∞∞‡∞æ‡∞Æ‡±Ä‡∞£" : lang === "hi" ? "‡§ó‡•ç‡§∞‡§æ‡§Æ‡•Ä‡§£" : "Rural"}</option>
              <option value="urban">{lang === "te" ? "‡∞®‡∞ó‡∞∞" : lang === "hi" ? "‡§∂‡§π‡§∞‡•Ä" : "Urban"}</option>
            </select>
          )}

          {/* INCOME */}
          {q.key === "income" && (
            <select value={user.income} onChange={(e) => setUser({ ...user, income: e.target.value })}>
              <option value="">{TEXT.select[lang]}</option>
              <option value="low">Below ‚Çπ2.5 Lakh</option>
              <option value="mid">‚Çπ2.5 ‚Äì 5 Lakh</option>
              <option value="high">Above ‚Çπ5 Lakh</option>
            </select>
          )}

          {/* DISABILITY */}
          {q.key === "disability" && (
            <select value={user.disability} onChange={(e) => setUser({ ...user, disability: e.target.value })}>
              <option value="">{TEXT.select[lang]}</option>
              <option value="yes">{lang === "te" ? "‡∞Ö‡∞µ‡±Å‡∞®‡±Å" : lang === "hi" ? "‡§π‡§æ‡§Å" : "Yes"}</option>
              <option value="no">{lang === "te" ? "‡∞ï‡∞æ‡∞¶‡±Å" : lang === "hi" ? "‡§®‡§π‡•Ä‡§Ç" : "No"}</option>
            </select>
          )}

          <button onClick={() => listen(q.key)}>üé§ {TEXT.speak[lang]}</button>

          <div className="nav">
            <button onClick={() => qStep === 0 ? setStep(0) : setQStep(qStep - 1)}>
              {TEXT.back[lang]}
            </button>
            <button className="primary" onClick={() => qStep < QUESTIONS.length - 1 ? setQStep(qStep + 1) : setStep(2)}>
              {TEXT.next[lang]}
            </button>
          </div>
        </div>
      </div>
    );
  }

  /* üìä RESULTS */
 /* üìä RESULTS */
return (
  <div className="results-page">

    {/* TITLE AT TOP */}
    <h2 className="results-title">
      {TEXT.results[lang]}
    </h2>

    {/* HORIZONTAL SCHEME LIST */}
    <div className="scheme-row">
      {eligible.map((s) => (
        <div className="scheme" key={s.id}>
          <img
            src={schemeImages[s.image] || defaultImg}
            alt={s.name[lang]}
          />

          <h3>{s.name[lang]}</h3>

          <span
            className={`gov-badge ${
              s.government === "Telangana Government" ? "ts" : "central"
            }`}
          >
            üèõÔ∏è {s.government}
          </span>

          <p>{s.benefit[lang]}</p>

          <a
            href={s.officialLink}
            target="_blank"
            rel="noreferrer"
          >
            {lang === "te"
              ? "‡∞Æ‡∞∞‡∞ø‡∞®‡±ç‡∞®‡∞ø ‡∞µ‡∞ø‡∞µ‡∞∞‡∞æ‡∞≤‡±Å"
              : lang === "hi"
              ? "‡§Ö‡§ß‡§ø‡§ï ‡§ú‡§æ‡§®‡§ï‡§æ‡§∞‡•Ä"
              : "More Information"}
          </a>
        </div>
      ))}
    </div>

    {/* HOME BUTTON AT BOTTOM */}
    <div className="home-btn-wrapper">
      <button
        className="home-btn"
        onClick={() => {
          setStep(0);
          setQStep(0);
        }}
      >
        üè† {lang === "te"
          ? "‡∞π‡±ã‡∞Æ‡±ç ‡∞™‡±á‡∞ú‡±Ä"
          : lang === "hi"
          ? "‡§Æ‡•Å‡§ñ‡•ç‡§Ø ‡§™‡•É‡§∑‡•ç‡§†"
          : "Home Page"}
      </button>
    </div>

  </div>
);
}
