const API_KEY = process.env.REACT_APP_GEMINI_API_KEY;

export async function askGemini(userQuestion, lang = "en") {
  const prompt =
    lang === "te"
      ? `మీరు ఒక ప్రభుత్వ సంక్షేమ సహాయకులు. ఈ ప్రశ్నకు సులభంగా సమాధానం ఇవ్వండి:\n${userQuestion}`
      : lang === "hi"
      ? `आप एक सरकारी कल्याण सहायक हैं। इस प्रश्न का सरल उत्तर दें:\n${userQuestion}`
      : `You are a government welfare assistant. Answer this simply:\n${userQuestion}`;

  const response = await fetch(
    `https://generativelanguage.googleapis.com/v1/models/gemini-pro:generateContent?key=${API_KEY}`,
    {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        contents: [
          {
            parts: [{ text: prompt }]
          }
        ]
      })
    }
  );

  const data = await response.json();
  return data.candidates?.[0]?.content?.parts?.[0]?.text || "No response";
}
