import React, { useState, useEffect } from "react";
import { schemes } from "./data";

// Background
import bgDefault from "./assets/backgrounds/bg-default.jpg";

// Scheme images
import tsPost from "./assets/schemes/ts-postmatric.jpg";
import nspImg from "./assets/schemes/nsp.jpg";
import aicteImg from "./assets/schemes/aicte.jpg";
import pmkisanImg from "./assets/schemes/pmkisan.jpg";
import rythuImg from "./assets/schemes/rythubharosa.jpg";
import mahalaxmiImg from "./assets/schemes/mahalaxmi.jpg";
import aasaraImg from "./assets/schemes/aasara.jpg";
import ayushmanImg from "./assets/schemes/ayushman.jpg";
import defaultImg from "./assets/schemes/default.jpg";

const schemeImages = {
  "ts-postmatric": tsPost,
  nsp: nspImg,
  aicte: aicteImg,
  pmkisan: pmkisanImg,
  rythubharosa: rythuImg,
  mahalaxmi: mahalaxmiImg,
  aasara: aasaraImg,
  ayushman: ayushmanImg
};

const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;

/* ---------------- LANGUAGE TEXT ---------------- */
const TEXT = {
  title: { en: "Welfare Voice Assistant", te: "సంక్షేమ వాయిస్ అసిస్టెంట్", hi: "कल्याण वॉइस सहायक" },
  next: { en: "Next", te: "తర్వాత", hi: "अगला" },
  back: { en: "Back", te: "వెనక్కి", hi: "वापस" },
  finish: { en: "Finish", te: "ముగించండి", hi: "समाप्त करें" },
  speak: { en: "Tap to Speak", te: "మాట్లాడండి", hi: "बोलें" },
  results: { en: "Eligible Government Schemes", te: "అర్హమైన ప్రభుత్వ పథకాలు", hi: "पात्र सरकारी योजनाएँ" },
  more: { en: "More Information", te: "మరిన్ని వివరాలు", hi: "अधिक जानकारी" },
  basedOn: {
    en: "Based on your answers, these schemes are suitable for you",
    te: "మీ సమాధానాల ఆధారంగా ఈ పథకాలు సరిపోతాయి",
    hi: "आपके उत्तरों के आधार पर ये योजनाएँ उपयुक्त हैं"
  }
};

/* ---------------- QUESTIONS ORDER ---------------- */
const QUESTIONS = [
  { key: "gender", en: "What is your gender?", te: "మీ లింగం ఏమిటి?", hi: "आपका लिंग क्या है?" },
  { key: "occupation", en: "What is your occupation?", te: "మీ వృత్తి ఏమిటి?", hi: "आपका पेशा क्या है?" },
  { key: "disability", en: "Do you have a disability?", te: "మీకు వికలాంగత ఉందా?", hi: "क्या आपको विकलांगता है?" },
  { key: "income", en: "What is your income range?", te: "మీ ఆదాయం ఎంత?", hi: "आपकी आय कितनी है?" }
];

export default function App() {
  const [step, setStep] = useState(0); // 0 home, 1 questions, 2 results
  const [qStep, setQStep] = useState(0);
  const [lang, setLang] = useState("en");

  const [user, setUser] = useState({
    gender: "",
    occupation: "",
    disability: "",
    income: ""
  });

  /* ---------------- VOICE ---------------- */
  const speak = (text) => {
    if (!window.speechSynthesis) return;
    const msg = new SpeechSynthesisUtterance(text);
    msg.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    msg.rate = 0.9;
    speechSynthesis.cancel();
    speechSynthesis.speak(msg);
  };

  /* Auto speak question */
  useEffect(() => {
    if (step === 1) speak(QUESTIONS[qStep][lang]);
  }, [qStep, step]);

  /* ---------------- ELIGIBILITY ---------------- */
  const eligible = schemes.filter(
    (s) =>
      s.occupation.includes(user.occupation) &&
      (s.income === "any" || s.income === user.income)
  );

  /* ---------------- NEW FEATURE: MATCH REASON ---------------- */
  const getMatchReason = (scheme) => {
    const reasons = [];

    if (scheme.occupation.includes(user.occupation)) {
      reasons.push(
        lang === "te"
          ? "మీ వృత్తి సరిపోతుంది"
          : lang === "hi"
          ? "आपका पेशा मेल खाता है"
          : "Your occupation matches"
      );
    }

    if (scheme.income === "any" || scheme.income === user.income) {
      reasons.push(
        lang === "te"
          ? "మీ ఆదాయ పరిమితి సరిపోతుంది"
          : lang === "hi"
          ? "आपकी आय పర सीमा मेल खाती है"
          : "Your income range matches"
      );
    }

    return reasons.join(" • ");
  };

  /* ---------------- HOME ---------------- */
  if (step === 0) {
    return (
      <div className="hero" style={{ backgroundImage: `url(${bgDefault})` }}>
        <div className="card">
          <h1>{TEXT.title[lang]}</h1>

          <select onChange={(e) => setLang(e.target.value)}>
            <option value="en">English</option>
            <option value="te">తెలుగు</option>
            <option value="hi">हिंदी</option>
          </select>

          <button className="primary" onClick={() => setStep(1)}>
            {TEXT.next[lang]}
          </button>
        </div>
      </div>
    );
  }

  /* ---------------- QUESTIONS ---------------- */
  if (step === 1) {
    const q = QUESTIONS[qStep];

    return (
      <div className="page">
        <div className="question-container">
          <strong>{qStep + 1} / {QUESTIONS.length}</strong>
          <progress value={qStep + 1} max={QUESTIONS.length} />

          <div className="card">
            <h2>{q[lang]}</h2>

            <select
              value={user[q.key]}
              onChange={(e) => setUser({ ...user, [q.key]: e.target.value })}
            >
              <option value="">Select</option>
              {q.key === "gender" && (
                <>
                  <option value="male">Male</option>
                  <option value="female">Female</option>
                  <option value="other">Other</option>
                </>
              )}
              {q.key === "occupation" && (
                <>
                  <option value="student">Student</option>
                  <option value="farmer">Farmer</option>
                  <option value="homemaker">Homemaker</option>
                  <option value="elderly">Elderly</option>
                  <option value="poor">Unemployed / Poor</option>
                </>
              )}
              {q.key === "disability" && (
                <>
                  <option value="yes">Yes</option>
                  <option value="no">No</option>
                </>
              )}
              {q.key === "income" && (
                <>
                  <option value="low">Below ₹2.5 Lakh</option>
                  <option value="mid">₹2.5–5 Lakh</option>
                  <option value="high">Above ₹5 Lakh</option>
                </>
              )}
            </select>
          </div>

          <div className="nav-buttons">
            <button onClick={() => qStep > 0 ? setQStep(qStep - 1) : setStep(0)}>
              {TEXT.back[lang]}
            </button>
            <button
              className="primary"
              onClick={() =>
                qStep < QUESTIONS.length - 1 ? setQStep(qStep + 1) : setStep(2)
              }
            >
              {TEXT.next[lang]}
            </button>
          </div>
        </div>
      </div>
    );
  }

  /* ---------------- RESULTS (WITH NEW FEATURES) ---------------- */
  return (
    <div className="page">
      <h2>{TEXT.results[lang]}</h2>
      <p>{TEXT.basedOn[lang]}</p>

      {eligible.map((s) => (
        <div className="scheme" key={s.id}>
          <img src={schemeImages[s.image] || defaultImg} alt="" />
          <h3>{s.name[lang]}</h3>
          <p>{s.benefit[lang]}</p>

          {/* NEW: Match explanation */}
          <p style={{ fontSize: "13px", color: "#2563eb" }}>
            ✔ {getMatchReason(s)}
          </p>

          <a
            href={s.officialLink}
            target="_blank"
            rel="noreferrer"
            className="info-btn"
          >
            {TEXT.more[lang]}
          </a>
        </div>
      ))}

      {/* NEW: Back & Finish buttons */}
      <div className="nav-buttons" style={{ marginTop: "24px" }}>
        <button onClick={() => setStep(1)}>
          {TEXT.back[lang]}
        </button>
        <button className="primary" onClick={() => alert("Demo completed")}>
          {TEXT.finish[lang]}
        </button>
      </div>
    </div>
  );
}
