import React, { useState, useEffect } from "react";
import { schemes } from "./data";
import "./style.css";

/* BACKGROUND */
import bgDefault from "./assets/backgrounds/bg-default.jpg";

/* SCHEME IMAGES */
import tsPost from "./assets/schemes/ts-postmatric.jpg";
import nspImg from "./assets/schemes/nsp.jpg";
import aicteImg from "./assets/schemes/aicte.jpg";
import pmkisanImg from "./assets/schemes/pmkisan.jpg";
import rythuImg from "./assets/schemes/rythubharosa.jpg";
import aasaraImg from "./assets/schemes/aasara.jpg";
import ayushmanImg from "./assets/schemes/ayushman.jpg";
import mahalaxmiImg from "./assets/schemes/mahalaxmi.jpg";
import gruhaJyothiImg from "./assets/schemes/gruha-jyothi.jpg";
import defaultImg from "./assets/schemes/default.jpg";

/* IMAGE MAP */
const schemeImages = {
  "ts-postmatric": tsPost,
  nsp: nspImg,
  aicte: aicteImg,
  pmkisan: pmkisanImg,
  rythubharosa: rythuImg,
  aasara: aasaraImg,
  ayushman: ayushmanImg,
  mahalaxmi: mahalaxmiImg,
  "gruha-jyothi": gruhaJyothiImg
};

/* SAFE SPEECH RECOGNITION */
const SR =
  typeof window !== "undefined"
    ? window.SpeechRecognition || window.webkitSpeechRecognition
    : null;

/* TEXT */
const TEXT = {
  title: {
    en: "Welfare Voice Assistant",
    te: "‡∞∏‡∞Ç‡∞ï‡±ç‡∞∑‡±á‡∞Æ ‡∞µ‡∞æ‡∞Ø‡∞ø‡∞∏‡±ç ‡∞Ö‡∞∏‡∞ø‡∞∏‡±ç‡∞ü‡±Ü‡∞Ç‡∞ü‡±ç",
    hi: "‡§ï‡§≤‡•ç‡§Ø‡§æ‡§£ ‡§µ‡•â‡§á‡§∏ ‡§∏‡§π‡§æ‡§Ø‡§ï"
  },
  start: { en: "Start", te: "‡∞™‡±ç‡∞∞‡∞æ‡∞∞‡∞Ç‡∞≠‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø", hi: "‡§∂‡•Å‡§∞‡•Ç ‡§ï‡§∞‡•á‡§Ç" },
  next: { en: "Next", te: "‡∞§‡∞∞‡±ç‡∞µ‡∞æ‡∞§", hi: "‡§Ö‡§ó‡§≤‡§æ" },
  back: { en: "Back", te: "‡∞µ‡±Ü‡∞®‡∞ï‡±ç‡∞ï‡∞ø", hi: "‡§µ‡§æ‡§™‡§∏" },
  speak: { en: "Tap to Speak", te: "‡∞Æ‡∞æ‡∞ü‡±ç‡∞≤‡∞æ‡∞°‡∞Ç‡∞°‡∞ø", hi: "‡§¨‡•ã‡§≤‡•á‡§Ç" },
  select: { en: "Select", te: "‡∞é‡∞Ç‡∞ö‡±Å‡∞ï‡±ã‡∞Ç‡∞°‡∞ø", hi: "‡§ö‡•Å‡§®‡•á‡§Ç" },
  results: {
    en: "Eligible Government Schemes",
    te: "‡∞Ö‡∞∞‡±ç‡∞π‡∞Æ‡±à‡∞® ‡∞™‡±ç‡∞∞‡∞≠‡±Å‡∞§‡±ç‡∞µ ‡∞™‡∞•‡∞ï‡∞æ‡∞≤‡±Å",
    hi: "‡§™‡§æ‡§§‡•ç‡§∞ ‡§∏‡§∞‡§ï‡§æ‡§∞‡•Ä ‡§Ø‡•ã‡§ú‡§®‡§æ‡§è‡§Å"
  }
};

/* QUESTIONS */
const QUESTIONS = [
  { key: "gender", en: "What is your gender?", te: "‡∞Æ‡±Ä ‡∞≤‡∞ø‡∞Ç‡∞ó‡∞Ç ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§≤‡§ø‡§Ç‡§ó ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "age", en: "What is your age group?", te: "‡∞Æ‡±Ä ‡∞µ‡∞Ø‡∞∏‡±ç‡∞∏‡±Å ‡∞µ‡∞∞‡±ç‡∞ó‡∞Ç?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§Ü‡§Ø‡•Å ‡§µ‡§∞‡•ç‡§ó?" },
  { key: "occupation", en: "What is your occupation?", te: "‡∞Æ‡±Ä ‡∞µ‡±É‡∞§‡±ç‡∞§‡∞ø ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§™‡•á‡§∂‡§æ ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "area", en: "Where do you live?", te: "‡∞Æ‡±Ä ‡∞®‡∞ø‡∞µ‡∞æ‡∞∏ ‡∞™‡±ç‡∞∞‡∞æ‡∞Ç‡∞§‡∞Ç?", hi: "‡§Ü‡§™ ‡§ï‡§π‡§æ‡§Å ‡§∞‡§π‡§§‡•á ‡§π‡•à‡§Ç?" },
  { key: "income", en: "What is your income range?", te: "‡∞Æ‡±Ä ‡∞Ü‡∞¶‡∞æ‡∞Ø‡∞Ç ‡∞é‡∞Ç‡∞§?", hi: "‡§Ü‡§™‡§ï‡•Ä ‡§Ü‡§Ø ‡§ï‡§ø‡§§‡§®‡•Ä ‡§π‡•à?" },
  { key: "disability", en: "Do you have a disability?", te: "‡∞Æ‡±Ä‡∞ï‡±Å ‡∞µ‡∞ø‡∞ï‡∞≤‡∞æ‡∞Ç‡∞ó‡∞§ ‡∞â‡∞Ç‡∞¶‡∞æ?", hi: "‡§ï‡•ç‡§Ø‡§æ ‡§Ü‡§™‡§ï‡•ã ‡§µ‡§ø‡§ï‡§≤‡§æ‡§Ç‡§ó‡§§‡§æ ‡§π‡•à?" }
];

export default function App() {
  /* STATE */
  const [step, setStep] = useState(0);
  const [qStep, setQStep] = useState(0);
  const [lang, setLang] = useState("en");

  const [user, setUser] = useState({
    name: "",
    gender: "",
    age: "",
    occupation: "",
    area: "",
    income: "",
    disability: ""
  });

  const [isSpeaking, setIsSpeaking] = useState(false);
  const [voiceIndex, setVoiceIndex] = useState(0);
  const [introSpoken, setIntroSpoken] = useState(false);

  /* SPEAK TEXT */
  const speakText = (text) => {
    if (!window.speechSynthesis) return;
    window.speechSynthesis.cancel();

    const u = new SpeechSynthesisUtterance(text);
    u.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    u.rate = 0.9;

    window.speechSynthesis.speak(u);
  };

  /* AUTO QUESTION VOICE */
  useEffect(() => {
    if (step === 1) {
      speakText(QUESTIONS[qStep][lang]);
    }
  }, [step, qStep, lang]);

  /* VOICE INPUT */
  const startListening = () => {
    if (!SR) {
      alert("Speech Recognition not supported");
      return;
    }

    const rec = new SR();
    rec.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    rec.start();

    rec.onresult = (e) => {
      const text = e.results[0][0].transcript.toLowerCase();
      const key = QUESTIONS[qStep].key;

      setUser((prev) => {
        const u = { ...prev };

        if (key === "gender") {
          if (text.includes("male")) u.gender = "male";
          if (text.includes("female")) u.gender = "female";
        }

        if (key === "disability") {
          if (text.includes("yes")) u.disability = "yes";
          if (text.includes("no")) u.disability = "no";
        }

        return u;
      });
    };
  };

  /* ELIGIBILITY */
  const eligible = schemes.filter((s) => {
    return (
      (s.occupation.includes("any") || s.occupation.includes(user.occupation)) &&
      (s.income.includes("any") || s.income.includes(user.income)) &&
      (s.disability.includes("any") || s.disability.includes(user.disability))
    );
  });

  /* RESULTS VOICE */
  const startVoiceForSchemes = () => {
    setIntroSpoken(false);
    setVoiceIndex(0);
    setIsSpeaking(true);
  };

  useEffect(() => {
    if (!isSpeaking) return;

    if (!introSpoken) {
      speakText(
        lang === "te"
          ? "‡∞Æ‡±Ä ‡∞µ‡∞ø‡∞µ‡∞∞‡∞æ‡∞≤ ‡∞Ü‡∞ß‡∞æ‡∞∞‡∞Ç‡∞ó‡∞æ ‡∞Ö‡∞∞‡±ç‡∞π‡∞Æ‡±à‡∞® ‡∞™‡∞•‡∞ï‡∞æ‡∞≤‡±Å ‡∞á‡∞µ‡∞ø."
          : lang === "hi"
          ? "‡§Ü‡§™‡§ï‡•á ‡§≤‡§ø‡§è ‡§™‡§æ‡§§‡•ç‡§∞ ‡§Ø‡•ã‡§ú‡§®‡§æ‡§è‡§Å ‡§Ø‡•á ‡§π‡•à‡§Ç‡•§"
          : "Based on your details, these are eligible schemes."
      );
      setIntroSpoken(true);
      return;
    }

    if (voiceIndex >= eligible.length) {
      setIsSpeaking(false);
      return;
    }

    const s = eligible[voiceIndex];
    speakText(`${s.name[lang]}. ${s.benefit[lang]}`);
    setVoiceIndex((v) => v + 1);
  }, [isSpeaking, introSpoken, voiceIndex, eligible, lang]);

  /* HOME */
  if (step === 0) {
    return (
      <div className="hero" style={{ backgroundImage: `url(${bgDefault})` }}>
        <div className="card center">
          <h1>{TEXT.title[lang]}</h1>
          <input
            placeholder="Enter your name"
            value={user.name}
            onChange={(e) => setUser({ ...user, name: e.target.value })}
          />
          <select value={lang} onChange={(e) => setLang(e.target.value)}>
            <option value="en">English</option>
            <option value="te">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            <option value="hi">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
          </select>
          <button className="primary" onClick={() => setStep(1)}>
            {TEXT.start[lang]}
          </button>
        </div>
      </div>
    );
  }

  /* QUESTIONS */
  if (step === 1) {
    const q = QUESTIONS[qStep];

    return (
      <div className="page center">
        <div className="card center">
          <h2>{q[lang]}</h2>

          <select
            value={user[q.key]}
            onChange={(e) => setUser({ ...user, [q.key]: e.target.value })}
          >
            <option value="">{TEXT.select[lang]}</option>
          </select>

          <button className="voice-btn" onClick={startListening}>
            üé§ {TEXT.speak[lang]}
          </button>

          <div className="nav">
            <button onClick={() => qStep === 0 ? setStep(0) : setQStep(qStep - 1)}>
              {TEXT.back[lang]}
            </button>
            <button
              className="primary"
              onClick={() =>
                qStep < QUESTIONS.length - 1
                  ? setQStep(qStep + 1)
                  : setStep(2)
              }
            >
              {TEXT.next[lang]}
            </button>
          </div>
        </div>
      </div>
    );
  }

  /* RESULTS */
  return (
    <div className="results-page">
      <h2>{TEXT.results[lang]}</h2>

      <div className="voice-controls">
        <button onClick={startVoiceForSchemes}>üîä Start Voice</button>
      </div>

      <div className="scheme-row">
        {eligible.map((s) => (
          <div className="scheme" key={s.id}>
            <img src={schemeImages[s.image] || defaultImg} alt={s.name[lang]} />
            <h3>{s.name[lang]}</h3>
            <p>{s.benefit[lang]}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
