import React, { useState, useEffect } from "react";
import { schemes } from "./data";
import "./style.css";

// Background
import bgDefault from "./assets/backgrounds/bg-default.jpg";

// Scheme Images
import tsPost from "./assets/schemes/ts-postmatric.jpg";
import nspImg from "./assets/schemes/nsp.jpg";
import aicteImg from "./assets/schemes/aicte.jpg";
import pmkisanImg from "./assets/schemes/pmkisan.jpg";
import rythuImg from "./assets/schemes/rythubharosa.jpg";
import aasaraImg from "./assets/schemes/aasara.jpg";
import ayushmanImg from "./assets/schemes/ayushman.jpg";
import mahalaxmiImg from "./assets/schemes/mahalaxmi.jpg";
import gruhaJyothiImg from "./assets/schemes/gruha-jyothi.jpg";
import defaultImg from "./assets/schemes/default.jpg";
const [isSpeaking, setIsSpeaking] = useState(false);
const [voiceIndex, setVoiceIndex] = useState(0);


/* IMAGE MAP */
const schemeImages = {
  "ts-postmatric": tsPost,
  nsp: nspImg,
  aicte: aicteImg,
  pmkisan: pmkisanImg,
  rythubharosa: rythuImg,
  aasara: aasaraImg,
  ayushman: ayushmanImg,
  mahalaxmi: mahalaxmiImg,
  "gruha-jyothi": gruhaJyothiImg
};

const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;

/* TEXT */
const TEXT = {
  title: {
    en: "Welfare Voice Assistant",
    te: "‡∞∏‡∞Ç‡∞ï‡±ç‡∞∑‡±á‡∞Æ ‡∞µ‡∞æ‡∞Ø‡∞ø‡∞∏‡±ç ‡∞Ö‡∞∏‡∞ø‡∞∏‡±ç‡∞ü‡±Ü‡∞Ç‡∞ü‡±ç",
    hi: "‡§ï‡§≤‡•ç‡§Ø‡§æ‡§£ ‡§µ‡•â‡§á‡§∏ ‡§∏‡§π‡§æ‡§Ø‡§ï"
  },
  start: { en: "Start", te: "‡∞™‡±ç‡∞∞‡∞æ‡∞∞‡∞Ç‡∞≠‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø", hi: "‡§∂‡•Å‡§∞‡•Ç ‡§ï‡§∞‡•á‡§Ç" },
  next: { en: "Next", te: "‡∞§‡∞∞‡±ç‡∞µ‡∞æ‡∞§", hi: "‡§Ö‡§ó‡§≤‡§æ" },
  back: { en: "Back", te: "‡∞µ‡±Ü‡∞®‡∞ï‡±ç‡∞ï‡∞ø", hi: "‡§µ‡§æ‡§™‡§∏" },
  speak: { en: "Tap to Speak", te: "‡∞Æ‡∞æ‡∞ü‡±ç‡∞≤‡∞æ‡∞°‡∞Ç‡∞°‡∞ø", hi: "‡§¨‡•ã‡§≤‡•á‡§Ç" },
  select: { en: "Select", te: "‡∞é‡∞Ç‡∞ö‡±Å‡∞ï‡±ã‡∞Ç‡∞°‡∞ø", hi: "‡§ö‡•Å‡§®‡•á‡§Ç" },
  results: {
    en: "Eligible Government Schemes",
    te: "‡∞Ö‡∞∞‡±ç‡∞π‡∞Æ‡±à‡∞® ‡∞™‡±ç‡∞∞‡∞≠‡±Å‡∞§‡±ç‡∞µ ‡∞™‡∞•‡∞ï‡∞æ‡∞≤‡±Å",
    hi: "‡§™‡§æ‡§§‡•ç‡§∞ ‡§∏‡§∞‡§ï‡§æ‡§∞‡•Ä ‡§Ø‡•ã‡§ú‡§®‡§æ‡§è‡§Å"
  }
};

/* QUESTIONS */
const QUESTIONS = [
  { key: "gender", en: "What is your gender?", te: "‡∞Æ‡±Ä ‡∞≤‡∞ø‡∞Ç‡∞ó‡∞Ç ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§≤‡§ø‡§Ç‡§ó ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "age", en: "What is your age?", te: "‡∞Æ‡±Ä ‡∞µ‡∞Ø‡∞∏‡±ç‡∞∏‡±Å ‡∞é‡∞Ç‡∞§?", hi: "‡§Ü‡§™‡§ï‡•Ä ‡§â‡§Æ‡•ç‡§∞ ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "occupation", en: "What is your occupation?", te: "‡∞Æ‡±Ä ‡∞µ‡±É‡∞§‡±ç‡∞§‡∞ø ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§™‡•á‡§∂‡§æ ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "area", en: "Where do you live?", te: "‡∞Æ‡±Ä ‡∞®‡∞ø‡∞µ‡∞æ‡∞∏ ‡∞™‡±ç‡∞∞‡∞æ‡∞Ç‡∞§‡∞Ç ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™ ‡§ï‡§π‡§æ‡§Å ‡§∞‡§π‡§§‡•á ‡§π‡•à‡§Ç?" },
  { key: "income", en: "What is your income range?", te: "‡∞Æ‡±Ä ‡∞Ü‡∞¶‡∞æ‡∞Ø‡∞Ç ‡∞é‡∞Ç‡∞§?", hi: "‡§Ü‡§™‡§ï‡•Ä ‡§Ü‡§Ø ‡§ï‡§ø‡§§‡§®‡•Ä ‡§π‡•à?" },
  { key: "disability", en: "Do you have a disability?", te: "‡∞Æ‡±Ä‡∞ï‡±Å ‡∞µ‡∞ø‡∞ï‡∞≤‡∞æ‡∞Ç‡∞ó‡∞§ ‡∞â‡∞Ç‡∞¶‡∞æ?", hi: "‡§ï‡•ç‡§Ø‡§æ ‡§Ü‡§™‡§ï‡•ã ‡§µ‡§ø‡§ï‡§≤‡§æ‡§Ç‡§ó‡§§‡§æ ‡§π‡•à?" }
];

export default function App() {
  const [step, setStep] = useState(0);
  const [qStep, setQStep] = useState(0);
  const [lang, setLang] = useState("en");
  const [isSpeaking, setIsSpeaking] = useState(false);

  const [user, setUser] = useState({
    name: "",
    gender: "",
    age: "",
    occupation: "",
    area: "",
    income: "",
    disability: ""
  });

  /* üîä SPEAK */
  const speakText = (text) => {
  if (!window.speechSynthesis) return;

  speechSynthesis.cancel();

  const utterance = new SpeechSynthesisUtterance(text);
  utterance.lang =
    lang === "te" ? "te-IN" :
    lang === "hi" ? "hi-IN" :
    "en-IN";

  utterance.rate = 0.9;

  utterance.onstart = () => setIsSpeaking(true);
  utterance.onend = () => setIsSpeaking(false);

  speechSynthesis.speak(utterance);
};

  const stopVoice = () => {
    speechSynthesis.cancel();
    setIsSpeaking(false);
  };


  /* üé§ LISTEN */
  const listen = (field) => {
    if (!SpeechRecognition) {
      speak("Voice not supported");
      return;
    }

    const r = new SpeechRecognition();
    r.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    r.start();

    speak(
      lang === "te" ? "‡∞¶‡∞Ø‡∞ö‡±á‡∞∏‡∞ø ‡∞Æ‡∞æ‡∞ü‡±ç‡∞≤‡∞æ‡∞°‡∞Ç‡∞°‡∞ø" :
      lang === "hi" ? "‡§ï‡•É‡§™‡§Ø‡§æ ‡§¨‡•ã‡§≤‡§ø‡§è" :
      "Please speak now"
    );

    r.onresult = (e) => {
      setUser({ ...user, [field]: e.results[0][0].transcript.toLowerCase() });
      speak("Answer recorded");
    };
  };

  /* AUTO SPEAK QUESTION */
  useEffect(() => {
    if (step === 1) {
      setTimeout(() => speak(QUESTIONS[qStep][lang]), 300);
    }
  }, [qStep, step, lang]);

  /* ELIGIBLE SCHEMES */
  const eligible = schemes.filter((s) => {
    const occ = s.occupation.includes("any") || s.occupation.includes(user.occupation);
    const inc = s.income.includes("any") || s.income.includes(user.income);
    const dis = s.disability.includes("any") || s.disability.includes(user.disability);
    return occ && inc && dis;
  });

  const startVoiceForSchemes = () => {
    if (eligible.length === 0) {
      speak("No eligible schemes found");
      return;
    }

    stopVoice();
    eligible.forEach((s, i) => {
      setTimeout(() => {
        speak(`${s.name[lang]}. ${s.benefit[lang]}`);
      }, i * 4500);
    });
  };

  /* HOME */
  if (step === 0) {
    return (
      <div className="hero" style={{ backgroundImage: `url(${bgDefault})` }}>
        <div className="card center">
          <h1>{TEXT.title[lang]}</h1>

          <input
            placeholder="Enter your name"
            value={user.name}
            onChange={(e) => setUser({ ...user, name: e.target.value })}
          />

          <select value={lang} onChange={(e) => setLang(e.target.value)}>
            <option value="en">English</option>
            <option value="te">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            <option value="hi">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
          </select>

          <button className="primary" onClick={() => setStep(1)}>
            {TEXT.start[lang]}
          </button>
        </div>
      </div>
    );
  }

  /* QUESTIONS */
  if (step === 1) {
    const q = QUESTIONS[qStep];

    return (
      <div className="page center">
        <div className="card center">
          <div className="step-indicator">
            Step {qStep + 1} / {QUESTIONS.length}
          </div>

          <h2>{q[lang]}</h2>

          <select
  value={user[q.key] || ""}
  onChange={(e) => setUser({ ...user, [q.key]: e.target.value })}
>
  <option value="">{TEXT.select[lang]}</option>

  {/* GENDER */}
  {q.key === "gender" && (
    <>
      <option value="male">{lang === "te" ? "‡∞™‡±Å‡∞∞‡±Å‡∞∑‡±Å‡∞°‡±Å" : lang === "hi" ? "‡§™‡•Å‡§∞‡•Å‡§∑" : "Male"}</option>
      <option value="female">{lang === "te" ? "‡∞∏‡±ç‡∞§‡±ç‡∞∞‡±Ä" : lang === "hi" ? "‡§Æ‡§π‡§ø‡§≤‡§æ" : "Female"}</option>
      <option value="other">{lang === "te" ? "‡∞á‡∞§‡∞∞‡±Å‡∞≤‡±Å" : lang === "hi" ? "‡§Ö‡§®‡•ç‡§Ø" : "Other"}</option>
    </>
  )}

  {/* ‚úÖ AGE (FIXED) */}
  {q.key === "age" && (
    <>
      <option value="below18">
        {lang === "te" ? "18 ‡∞≤‡±ã‡∞™‡±Å" : lang === "hi" ? "18 ‡§∏‡•á ‡§ï‡§Æ" : "Below 18"}
      </option>
      <option value="18-35">
        {lang === "te" ? "18 ‚Äì 35" : lang === "hi" ? "18 ‚Äì 35" : "18 ‚Äì 35"}
      </option>
      <option value="36-59">
        {lang === "te" ? "36 ‚Äì 59" : lang === "hi" ? "36 ‚Äì 59" : "36 ‚Äì 59"}
      </option>
      <option value="60+">
        {lang === "te" ? "60 ‡∞™‡±à‡∞¨‡∞°‡∞ø‡∞® ‡∞µ‡∞æ‡∞∞‡±Å" : lang === "hi" ? "60 ‡§∏‡•á ‡§ä‡§™‡§∞" : "60+"}
      </option>
    </>
  )}

  {/* OCCUPATION */}
  {q.key === "occupation" && (
  <select
    value={user.occupation}
    onChange={(e) =>
      setUser({ ...user, occupation: e.target.value })
    }
  >
    <option value="">
      {lang === "te"
        ? "‡∞é‡∞Ç‡∞ö‡±Å‡∞ï‡±ã‡∞Ç‡∞°‡∞ø"
        : lang === "hi"
        ? "‡§ö‡•Å‡§®‡•á‡§Ç"
        : "Select"}
    </option>

    <option value="student">
      {lang === "te"
        ? "‡∞µ‡∞ø‡∞¶‡±ç‡∞Ø‡∞æ‡∞∞‡±ç‡∞•‡∞ø"
        : lang === "hi"
        ? "‡§õ‡§æ‡§§‡•ç‡§∞"
        : "Student"}
    </option>

    <option value="farmer">
      {lang === "te"
        ? "‡∞∞‡±à‡∞§‡±Å"
        : lang === "hi"
        ? "‡§ï‡§ø‡§∏‡§æ‡§®"
        : "Farmer"}
    </option>

    <option value="labourer">
      {lang === "te"
        ? "‡∞ï‡±Ç‡∞≤‡±Ä"
        : lang === "hi"
        ? "‡§Æ‡§ú‡§º‡§¶‡•Ç‡§∞"
        : "Labourer"}
    </option>

    <option value="elderly">
      {lang === "te"
        ? "‡∞µ‡±É‡∞¶‡±ç‡∞ß‡±Å‡∞°‡±Å"
        : lang === "hi"
        ? "‡§µ‡•É‡§¶‡•ç‡§ß"
        : "Elderly"}
    </option>

    <option value="poor">
      {lang === "te"
        ? "‡∞™‡±á‡∞¶ / ‡∞®‡∞ø‡∞∞‡±Å‡∞¶‡±ç‡∞Ø‡±ã‡∞ó‡∞ø"
        : lang === "hi"
        ? "‡§ó‡§∞‡•Ä‡§¨ / ‡§¨‡•á‡§∞‡•ã‡§ú‡§º‡§ó‡§æ‡§∞"
        : "Unemployed / Poor"}
    </option>
  </select>
)}

  {/* AREA */}
  {q.key === "area" && (
  <select
    value={user.area}
    onChange={(e) => setUser({ ...user, area: e.target.value })}
  >
    <option value="">
      {lang === "te" ? "‡∞é‡∞Ç‡∞ö‡±Å‡∞ï‡±ã‡∞Ç‡∞°‡∞ø" : lang === "hi" ? "‡§ö‡•Å‡§®‡•á‡§Ç" : "Select"}
    </option>
    <option value="rural">
      {lang === "te" ? "‡∞ó‡±ç‡∞∞‡∞æ‡∞Æ‡±Ä‡∞£" : lang === "hi" ? "‡§ó‡•ç‡§∞‡§æ‡§Æ‡•Ä‡§£" : "Rural"}
    </option>
    <option value="urban">
      {lang === "te" ? "‡∞®‡∞ó‡∞∞" : lang === "hi" ? "‡§∂‡§π‡§∞‡•Ä" : "Urban"}
    </option>
  </select>
)}


  {/* INCOME */}
  {q.key === "income" && (
  <select
    value={user.income}
    onChange={(e) => setUser({ ...user, income: e.target.value })}
  >
    <option value="">
      {lang === "te" ? "‡∞é‡∞Ç‡∞ö‡±Å‡∞ï‡±ã‡∞Ç‡∞°‡∞ø" : lang === "hi" ? "‡§ö‡•Å‡§®‡•á‡§Ç" : "Select"}
    </option>
    <option value="low">
      {lang === "te" ? "‚Çπ2.5 ‡∞≤‡∞ï‡±ç‡∞∑‡∞≤ ‡∞≤‡±ã‡∞™‡±Å" : lang === "hi" ? "‚Çπ2.5 ‡§≤‡§æ‡§ñ ‡§∏‡•á ‡§ï‡§Æ" : "Below ‚Çπ2.5 Lakh"}
    </option>
    <option value="mid">
      {lang === "te" ? "‚Çπ2.5 ‚Äì 5 ‡∞≤‡∞ï‡±ç‡∞∑‡∞≤‡±Å" : lang === "hi" ? "‚Çπ2.5 ‚Äì 5 ‡§≤‡§æ‡§ñ" : "‚Çπ2.5 ‚Äì 5 Lakh"}
    </option>
    <option value="high">
      {lang === "te" ? "‚Çπ5 ‡∞≤‡∞ï‡±ç‡∞∑‡∞≤‡∞ï‡±Å ‡∞™‡±à‡∞ó‡∞æ" : lang === "hi" ? "‚Çπ5 ‡§≤‡§æ‡§ñ ‡§∏‡•á ‡§Ö‡§ß‡§ø‡§ï" : "Above ‚Çπ5 Lakh"}
    </option>
  </select>
)}


  {/* DISABILITY */}
  {q.key === "disability" && (
    <>
      <option value="yes">{lang === "te" ? "‡∞Ö‡∞µ‡±Å‡∞®‡±Å" : lang === "hi" ? "‡§π‡§æ‡§Å" : "Yes"}</option>
      <option value="no">{lang === "te" ? "‡∞ï‡∞æ‡∞¶‡±Å" : lang === "hi" ? "‡§®‡§π‡•Ä‡§Ç" : "No"}</option>
    </>
  )}
</select>


          <button onClick={() => listen(q.key)}>
            üé§ {TEXT.speak[lang]}
          </button>

          <div className="nav">
            <button onClick={() => qStep === 0 ? setStep(0) : setQStep(qStep - 1)}>
              {TEXT.back[lang]}
            </button>
            <button className="primary" onClick={() =>
              qStep < QUESTIONS.length - 1 ? setQStep(qStep + 1) : setStep(2)
            }>
              {TEXT.next[lang]}
            </button>
          </div>
        </div>
      </div>
    );
  }

  /* RESULTS */
  return (
    <div className="results-page">
      <h2>{TEXT.results[lang]}</h2>

      <div className="voice-controls">
        <button onClick={startVoiceForSchemes}>üîä Start Voice</button>
        <button onClick={stopVoice}>‚èπ Stop Voice</button>
      </div>

      <div className="scheme-row">
        {eligible.map((s) => (
          <div className="scheme" key={s.id}>
            <img src={schemeImages[s.image] || defaultImg} alt="" />
            <h3>{s.name[lang]}</h3>
            <p>{s.benefit[lang]}</p>
            <a href={s.officialLink} target="_blank" rel="noreferrer">
              More Information
            </a>
          </div>
        ))}
      </div>

      <button className="home-btn" onClick={() => {
        setStep(0);
        setQStep(0);
      }}>
        üè† Home Page
      </button>
    </div>
  );
}
