const QUESTIONS = [
  {
    key: "gender",
    en: "What is your gender?",
    te: "‡∞Æ‡±Ä ‡∞≤‡∞ø‡∞Ç‡∞ó‡∞Ç ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?",
    hi: "‡§Ü‡§™‡§ï‡§æ ‡§≤‡§ø‡§Ç‡§ó ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?"
  },
  {
    key: "ageGroup",
    en: "What is your age group?",
    te: "‡∞Æ‡±Ä ‡∞µ‡∞Ø‡∞∏‡±ç‡∞∏‡±Å ‡∞µ‡∞∞‡±ç‡∞ó‡∞Ç ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?",
    hi: "‡§Ü‡§™‡§ï‡§æ ‡§Ü‡§Ø‡•Å ‡§µ‡§∞‡•ç‡§ó ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?"
  },
  {
    key: "occupation",
    en: "What is your occupation?",
    te: "‡∞Æ‡±Ä ‡∞µ‡±É‡∞§‡±ç‡∞§‡∞ø ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?",
    hi: "‡§Ü‡§™‡§ï‡§æ ‡§™‡•á‡§∂‡§æ ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?"
  },
  {
    key: "income",
    en: "What is your income range?",
    te: "‡∞Æ‡±Ä ‡∞Ü‡∞¶‡∞æ‡∞Ø‡∞Ç ‡∞é‡∞Ç‡∞§?",
    hi: "‡§Ü‡§™‡§ï‡•Ä ‡§Ü‡§Ø ‡§ï‡§ø‡§§‡§®‡•Ä ‡§π‡•à?"
  },
  {
    key: "area",
    en: "Where do you live?",
    te: "‡∞Æ‡±Ä‡∞∞‡±Å ‡∞é‡∞ï‡±ç‡∞ï‡∞° ‡∞®‡∞ø‡∞µ‡∞∏‡∞ø‡∞∏‡±ç‡∞§‡±Å‡∞®‡±ç‡∞®‡∞æ‡∞∞‡±Å?",
    hi: "‡§Ü‡§™ ‡§ï‡§π‡§æ‡§Å ‡§∞‡§π‡§§‡•á ‡§π‡•à‡§Ç?"
  },
  {
    key: "disability",
    en: "Do you have a disability?",
    te: "‡∞Æ‡±Ä‡∞ï‡±Å ‡∞µ‡∞ø‡∞ï‡∞≤‡∞æ‡∞Ç‡∞ó‡∞§ ‡∞â‡∞Ç‡∞¶‡∞æ?",
    hi: "‡§ï‡•ç‡§Ø‡§æ ‡§Ü‡§™‡§ï‡•ã ‡§µ‡§ø‡§ï‡§≤‡§æ‡§Ç‡§ó‡§§‡§æ ‡§π‡•à?"
  }
];

export default function App() {
  const [step, setStep] = useState(0);
  const [qIndex, setQIndex] = useState(0);
  const [lang, setLang] = useState("en");

  /* ---------- USER STATE (STANDARDIZED) ---------- */
  const [user, setUser] = useState({
    gender: "",
    ageGroup: "",
    occupation: "",
    income: "",
    area: "",
    disability: ""
  });

  /* ---------- TEXT TO SPEECH ---------- */
  const speak = (text) => {
    if (!window.speechSynthesis) return;
    window.speechSynthesis.cancel();
    const u = new SpeechSynthesisUtterance(text);
    u.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    u.rate = 0.9;
    window.speechSynthesis.speak(u);
  };

  /* ---------- AUTO SPEAK QUESTION ---------- */
 useEffect(() => {
  if (step === 2 && eligibleSchemes.length > 0) {
    const names = eligibleSchemes.map(s => s.name[lang]).join(", ");

    const intro =
      lang === "te"
        ? `‡∞Æ‡±Ä ‡∞Ö‡∞∞‡±ç‡∞π‡∞§ ‡∞Ü‡∞ß‡∞æ‡∞∞‡∞Ç‡∞ó‡∞æ ‡∞à ‡∞™‡∞•‡∞ï‡∞æ‡∞≤‡±Å ‡∞Ö‡∞Ç‡∞¶‡±Å‡∞¨‡∞æ‡∞ü‡±Å‡∞≤‡±ã ‡∞â‡∞®‡±ç‡∞®‡∞æ‡∞Ø‡∞ø. ${names}`
        : lang === "hi"
        ? `‡§Ü‡§™‡§ï‡•Ä ‡§™‡§æ‡§§‡•ç‡§∞‡§§‡§æ ‡§ï‡•á ‡§Ü‡§ß‡§æ‡§∞ ‡§™‡§∞ ‡§Ø‡•á ‡§Ø‡•ã‡§ú‡§®‡§æ‡§è‡§Å ‡§â‡§™‡§≤‡§¨‡•ç‡§ß ‡§π‡•à‡§Ç‡•§ ${names}`
        : `Based on your eligibility, these schemes are available: ${names}`;

    speak(intro);
  }
}, [step, lang]);



  /* ---------- VOICE INPUT ---------- */
  const startListening = () => {
    if (!SpeechRecognition) {
      alert("Speech Recognition not supported");
      return;
    }

    const rec = new SpeechRecognition();
    rec.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    rec.start();

    rec.onresult = (e) => {
      const speech = e.results[0][0].transcript.toLowerCase();
      const key = QUESTIONS[qIndex].key;

      let value = "";

      if (key === "gender") {
        if (speech.includes("male")) value = "male";
        if (speech.includes("female")) value = "female";
      }

      if (key === "disability") {
        if (speech.includes("yes")) value = "yes";
        if (speech.includes("no")) value = "no";
      }

      if (value) {
        setUser({ ...user, [key]: value });
      }
    };
  };

  /* ---------- ELIGIBILITY ENGINE (FIXED) ---------- */
  const eligibleSchemes = schemes.filter((scheme) => {
    const e = scheme.eligibility;

    return (
      (e.gender.includes("any") || e.gender.includes(user.gender)) &&
      (e.ageGroup.includes("any") || e.ageGroup.includes(user.ageGroup)) &&
      (e.occupation.includes("any") || e.occupation.includes(user.occupation)) &&
      (e.income.includes("any") || e.income.includes(user.income)) &&
      (e.disability.includes("any") || e.disability.includes(user.disability)) &&
      (e.area.includes("any") || e.area.includes(user.area))
    );
  });

  /* ---------- HOME ---------- */
  if (step === 0) {
    return (
      <div className="page center">
        <div className="card center">
          <h1>{TEXT.title[lang]}</h1>

          <select value={lang} onChange={(e) => setLang(e.target.value)}>
            <option value="en">English</option>
            <option value="te">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            <option value="hi">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
          </select>

          <button className="primary" onClick={() => setStep(1)}>
            {TEXT.start[lang]}
          </button>
        </div>
      </div>
    );
  }

  /* ---------- QUESTIONS ---------- */
  if (step === 1) {
    const q = QUESTIONS[qIndex];

    return (
      <div className="page center">
        <div className="card center">
          <h2>{q[lang]}</h2>

          <select
            value={user[q.key]}
            onChange={(e) =>
              setUser({ ...user, [q.key]: e.target.value })
            }
          >
            <option value="">Select</option>

            {q.key === "gender" && (
              <>
                <option value="male">Male</option>
                <option value="female">Female</option>
              </>
            )}

            {q.key === "ageGroup" && (
              <>
                <option value="below18">Below 18</option>
                <option value="18-35">18‚Äì35</option>
                <option value="36-59">36‚Äì59</option>
                <option value="60+">60+</option>
              </>
            )}

            {q.key === "occupation" && (
              <>
                <option value="student">Student</option>
                <option value="farmer">Farmer</option>
                <option value="homemaker">Homemaker</option>
                <option value="elderly">Elderly</option>
                <option value="unemployed">Unemployed</option>
              </>
            )}

            {q.key === "income" && (
              <>
                <option value="low">Low</option>
                <option value="mid">Middle</option>
                <option value="high">High</option>
              </>
            )}

            {q.key === "area" && (
              <>
                <option value="rural">Rural</option>
                <option value="urban">Urban</option>
              </>
            )}

            {q.key === "disability" && (
              <>
                <option value="yes">Yes</option>
                <option value="no">No</option>
              </>
            )}
          </select>

          <button onClick={startListening}>üé§ {TEXT.speak[lang]}</button>

          <div className="nav">
            <button
              onClick={() =>
                qIndex === 0 ? setStep(0) : setQIndex(qIndex - 1)
              }
            >
              {TEXT.back[lang]}
            </button>

            <button
              className="primary"
              onClick={() =>
                qIndex < QUESTIONS.length - 1
                  ? setQIndex(qIndex + 1)
                  : setStep(2)
              }
            >
              {TEXT.next[lang]}
            </button>
          </div>
        </div>
      </div>
    );
  }

  /* ---------- RESULTS ---------- */
  return (
    <div className="results-page">
      <h2>{TEXT.results[lang]}</h2>

      <div className="scheme-row">
        {eligibleSchemes.map((s) => (
          <div className="scheme-card" key={s.id}>
            <img
              src={schemeImages[s.image] || defaultImg}
              alt={s.name[lang]}
            />
            <h3>{s.name[lang]}</h3>
            <p>{s.benefit[lang]}</p>
            <a href={s.officialLink} target="_blank" rel="noreferrer">
              Official Website
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}
