import React, { useState, useEffect } from "react";
import { schemes } from "./data";
import "./style.css";

/* BACKGROUNDS */
import bgDefault from "./assets/backgrounds/bg-default.jpg";
import bgFarmer from "./assets/backgrounds/bg-farmer.jpg";
import bgWomen from "./assets/backgrounds/bg-women.jpg";
import bgElderly from "./assets/backgrounds/bg-elderly.jpg";

/* SCHEME IMAGES */
import tsPost from "./assets/schemes/ts-postmatric.jpg";
import nspImg from "./assets/schemes/nsp.jpg";
import aicteImg from "./assets/schemes/aicte.jpg";
import pmkisanImg from "./assets/schemes/pmkisan.jpg";
import rythuImg from "./assets/schemes/rythubharosa.jpg";
import aasaraImg from "./assets/schemes/aasara.jpg";
import ayushmanImg from "./assets/schemes/ayushman.jpg";
import mahalaxmiImg from "./assets/schemes/mahalaxmi.jpg";
import gruhaJyothiImg from "./assets/schemes/gruha-jyothi.jpg";
import defaultImg from "./assets/schemes/default.jpg";

/* IMAGE MAP */
const schemeImages = {
  "ts-postmatric": tsPost,
  nsp: nspImg,
  aicte: aicteImg,
  pmkisan: pmkisanImg,
  rythubharosa: rythuImg,
  aasara: aasaraImg,
  ayushman: ayushmanImg,
  mahalaxmi: mahalaxmiImg,
  "gruha-jyothi": gruhaJyothiImg
};

/* SPEECH RECOGNITION */
const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;

/* TEXT */
const TEXT = {
  title: {
    en: "Welfare Voice Assistant",
    te: "‡∞∏‡∞Ç‡∞ï‡±ç‡∞∑‡±á‡∞Æ ‡∞µ‡∞æ‡∞Ø‡∞ø‡∞∏‡±ç ‡∞Ö‡∞∏‡∞ø‡∞∏‡±ç‡∞ü‡±Ü‡∞Ç‡∞ü‡±ç",
    hi: "‡§ï‡§≤‡•ç‡§Ø‡§æ‡§£ ‡§µ‡•â‡§á‡§∏ ‡§∏‡§π‡§æ‡§Ø‡§ï"
  },
  start: { en: "Start", te: "‡∞™‡±ç‡∞∞‡∞æ‡∞∞‡∞Ç‡∞≠‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø", hi: "‡§∂‡•Å‡§∞‡•Ç ‡§ï‡§∞‡•á‡§Ç" },
  next: { en: "Next", te: "‡∞§‡∞∞‡±ç‡∞µ‡∞æ‡∞§", hi: "‡§Ö‡§ó‡§≤‡§æ" },
  back: { en: "Back", te: "‡∞µ‡±Ü‡∞®‡∞ï‡±ç‡∞ï‡∞ø", hi: "‡§µ‡§æ‡§™‡§∏" },
  speak: { en: "Speak", te: "‡∞Æ‡∞æ‡∞ü‡±ç‡∞≤‡∞æ‡∞°‡∞Ç‡∞°‡∞ø", hi: "‡§¨‡•ã‡§≤‡•á‡§Ç" },
  select: { en: "Select", te: "‡∞é‡∞Ç‡∞ö‡±Å‡∞ï‡±ã‡∞Ç‡∞°‡∞ø", hi: "‡§ö‡•Å‡§®‡•á‡§Ç" },
  results: {
    en: "Eligible Government Schemes",
    te: "‡∞Ö‡∞∞‡±ç‡∞π‡∞Æ‡±à‡∞® ‡∞™‡±ç‡∞∞‡∞≠‡±Å‡∞§‡±ç‡∞µ ‡∞™‡∞•‡∞ï‡∞æ‡∞≤‡±Å",
    hi: "‡§™‡§æ‡§§‡•ç‡§∞ ‡§∏‡§∞‡§ï‡§æ‡§∞‡•Ä ‡§Ø‡•ã‡§ú‡§®‡§æ‡§è‡§Å"
  }
};

/* QUESTIONS */
const QUESTIONS = [
  { key: "gender", en: "What is your gender?", te: "‡∞Æ‡±Ä ‡∞≤‡∞ø‡∞Ç‡∞ó‡∞Ç ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§≤‡§ø‡§Ç‡§ó ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "age", en: "What is your age group?", te: "‡∞Æ‡±Ä ‡∞µ‡∞Ø‡∞∏‡±ç‡∞∏‡±Å ‡∞µ‡∞∞‡±ç‡∞ó‡∞Ç?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§Ü‡§Ø‡•Å ‡§µ‡§∞‡•ç‡§ó?" },
  { key: "occupation", en: "What is your occupation?", te: "‡∞Æ‡±Ä ‡∞µ‡±É‡∞§‡±ç‡∞§‡∞ø ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§™‡•á‡§∂‡§æ ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "area", en: "Where do you live?", te: "‡∞Æ‡±Ä ‡∞®‡∞ø‡∞µ‡∞æ‡∞∏ ‡∞™‡±ç‡∞∞‡∞æ‡∞Ç‡∞§‡∞Ç?", hi: "‡§Ü‡§™ ‡§ï‡§π‡§æ‡§Å ‡§∞‡§π‡§§‡•á ‡§π‡•à‡§Ç?" },
  { key: "income", en: "What is your income range?", te: "‡∞Æ‡±Ä ‡∞Ü‡∞¶‡∞æ‡∞Ø‡∞Ç ‡∞é‡∞Ç‡∞§?", hi: "‡§Ü‡§™‡§ï‡•Ä ‡§Ü‡§Ø ‡§ï‡§ø‡§§‡§®‡•Ä ‡§π‡•à?" },
  { key: "disability", en: "Do you have a disability?", te: "‡∞Æ‡±Ä‡∞ï‡±Å ‡∞µ‡∞ø‡∞ï‡∞≤‡∞æ‡∞Ç‡∞ó‡∞§ ‡∞â‡∞Ç‡∞¶‡∞æ?", hi: "‡§ï‡•ç‡§Ø‡§æ ‡§Ü‡§™‡§ï‡•ã ‡§µ‡§ø‡§ï‡§≤‡§æ‡§Ç‡§ó‡§§‡§æ ‡§π‡•à?" }
];

export default function App() {
  const [step, setStep] = useState(0);
  const [qIndex, setQIndex] = useState(0);
  const [lang, setLang] = useState("en");

  const [user, setUser] = useState({
    name: "",
    gender: "",
    age: "",
    occupation: "",
    area: "",
    income: "",
    disability: ""
  });

  /* ---------- SPEAK ---------- */
  const speak = (text) => {
    if (!window.speechSynthesis) return;
    window.speechSynthesis.cancel();
    const u = new SpeechSynthesisUtterance(text);
    u.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    u.rate = 0.9;
    window.speechSynthesis.speak(u);
  };
  /* ================= SCHEME VOICE EXPLANATION ================= */

const explainSchemeByVoice = (scheme) => {
  let text = "";

  if (lang === "te") {
    text = `
      ${scheme.name.te}.
      ‡∞à ‡∞™‡∞•‡∞ï‡∞Ç ‡∞Æ‡±Å‡∞ñ‡±ç‡∞Ø ‡∞â‡∞¶‡±ç‡∞¶‡±á‡∞∂‡±ç‡∞Ø‡∞Ç ${scheme.benefit.te}.
      ‡∞á‡∞¶‡∞ø ‡∞Ö‡∞∞‡±ç‡∞π‡±Å‡∞≤‡±à‡∞® ‡∞™‡±ç‡∞∞‡∞ú‡∞≤‡∞ï‡±Å ‡∞™‡±ç‡∞∞‡∞≠‡±Å‡∞§‡±ç‡∞µ ‡∞∏‡∞π‡∞æ‡∞Ø‡∞æ‡∞®‡±ç‡∞®‡∞ø ‡∞Ö‡∞Ç‡∞¶‡∞ø‡∞Ç‡∞ö‡±á‡∞Ç‡∞¶‡±Å‡∞ï‡±Å ‡∞∞‡±Ç‡∞™‡±ä‡∞Ç‡∞¶‡∞ø‡∞Ç‡∞ö‡∞¨‡∞°‡∞ø‡∞Ç‡∞¶‡∞ø.
      ‡∞Æ‡∞∞‡∞ø‡∞®‡±ç‡∞®‡∞ø ‡∞µ‡∞ø‡∞µ‡∞∞‡∞æ‡∞≤‡∞ï‡±Å ‡∞Ö‡∞ß‡∞ø‡∞ï‡∞æ‡∞∞‡∞ø‡∞ï ‡∞µ‡±Ü‡∞¨‡±ç‚Äå‡∞∏‡±à‡∞ü‡±ç‚Äå‡∞®‡±Å ‡∞∏‡∞Ç‡∞¶‡∞∞‡±ç‡∞∂‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø.
    `;
  } 
  
  else if (lang === "hi") {
    text = `
      ${scheme.name.hi}.
      ‡§á‡§∏ ‡§Ø‡•ã‡§ú‡§®‡§æ ‡§ï‡§æ ‡§Æ‡•Å‡§ñ‡•ç‡§Ø ‡§â‡§¶‡•ç‡§¶‡•á‡§∂‡•ç‡§Ø ‡§π‡•à ${scheme.benefit.hi}.
      ‡§Ø‡§π ‡§Ø‡•ã‡§ú‡§®‡§æ ‡§™‡§æ‡§§‡•ç‡§∞ ‡§®‡§æ‡§ó‡§∞‡§ø‡§ï‡•ã‡§Ç ‡§ï‡•á ‡§≤‡§ø‡§è ‡§¨‡§®‡§æ‡§à ‡§ó‡§à ‡§π‡•à‡•§
      ‡§Ö‡§ß‡§ø‡§ï ‡§ú‡§æ‡§®‡§ï‡§æ‡§∞‡•Ä ‡§ï‡•á ‡§≤‡§ø‡§è ‡§Ü‡§ß‡§ø‡§ï‡§æ‡§∞‡§ø‡§ï ‡§µ‡•á‡§¨‡§∏‡§æ‡§á‡§ü ‡§¶‡•á‡§ñ‡•á‡§Ç‡•§
    `;
  } 
  
  else {
    text = `
      ${scheme.name.en}.
      The main goal of this scheme is ${scheme.benefit.en}.
      This scheme is designed to support eligible citizens.
      Please visit the official website for more details.
    `;
  }

  speak(text);
};


  /* AUTO QUESTION VOICE */
  useEffect(() => {
    if (step === 1) {
      speak(QUESTIONS[qIndex][lang]);
    }
  }, [step, qIndex, lang]);

  /* VOICE INPUT */
  const startListening = () => {
    if (!SpeechRecognition) {
      alert("Speech recognition not supported");
      return;
    }

    const rec = new SpeechRecognition();
    rec.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    rec.start();

    rec.onresult = (e) => {
      const speech = e.results[0][0].transcript.toLowerCase();
      const key = QUESTIONS[qIndex].key;

      let value = "";

      if (key === "gender") {
        if (speech.includes("male")) value = "male";
        if (speech.includes("female")) value = "female";
      }

      if (key === "disability") {
        if (speech.includes("yes")) value = "yes";
        if (speech.includes("no")) value = "no";
      }

      if (value) {
        setUser({ ...user, [key]: value });
      }
    };
  };

  /* BACKGROUND LOGIC */
  const getBackground = () => {
    if (user.occupation === "farmer") return bgFarmer;
    if (user.occupation === "elderly") return bgElderly;
    if (user.gender === "female") return bgWomen;
    return bgDefault;
  };

  /* ELIGIBILITY FILTER */
  const eligibleSchemes = schemes.filter((s) => {
  const e = s.eligibility;

  return (
    (e.gender.includes("any") || e.gender.includes(user.gender)) &&
    (e.ageGroup.includes("any") || e.ageGroup.includes(user.age)) &&
    (e.occupation.includes("any") || e.occupation.includes(user.occupation)) &&
    (e.income.includes("any") || e.income.includes(user.income)) &&
    (e.disability.includes("any") || e.disability.includes(user.disability)) &&
    (e.area.includes("any") || e.area.includes(user.area))
  );
});

  /* ---------- HOME ---------- */
  if (step === 0) {
    return (
      <div className="hero" style={{ backgroundImage: `url(${bgDefault})` }}>
        <div className="card center">
          <h1>{TEXT.title[lang]}</h1>

          <input
            placeholder="Enter your name"
            value={user.name}
            onChange={(e) => setUser({ ...user, name: e.target.value })}
          />

          <select value={lang} onChange={(e) => setLang(e.target.value)}>
            <option value="en">English</option>
            <option value="te">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            <option value="hi">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
          </select>

          <button className="primary" onClick={() => setStep(1)}>
            {TEXT.start[lang]}
          </button>
        </div>
      </div>
    );
  }

  /* ---------- QUESTIONS ---------- */
  if (step === 1) {
    const q = QUESTIONS[qIndex];

    return (
      <div className="page center" style={{ backgroundImage: `url(${getBackground()})` }}>
        <div className="card center">
          <h2>{q[lang]}</h2>

          <select
            value={user[q.key]}
            onChange={(e) => setUser({ ...user, [q.key]: e.target.value })}
          >
            <option value="" disabled>
              {TEXT.select[lang]}
            </option>

            {q.key === "gender" && (
    <>
      <option value="male">
        {lang === "te" ? "‡∞™‡±Å‡∞∞‡±Å‡∞∑‡±Å‡∞°‡±Å" : lang === "hi" ? "‡§™‡•Å‡§∞‡•Å‡§∑" : "Male"}
      </option>
      <option value="female">
        {lang === "te" ? "‡∞∏‡±ç‡∞§‡±ç‡∞∞‡±Ä" : lang === "hi" ? "‡§Æ‡§π‡§ø‡§≤‡§æ" : "Female"}
      </option>
      <option value="other">
        {lang === "te" ? "‡∞á‡∞§‡∞∞‡±Å‡∞≤‡±Å" : lang === "hi" ? "‡§Ö‡§®‡•ç‡§Ø" : "Other"}
      </option>
    </>
  )}

  {/* AGE */}
  {q.key === "age" && (
    <>
      <option value="below18">
        {lang === "te" ? "18 ‡∞≤‡±ã‡∞™‡±Å" : lang === "hi" ? "18 ‡§∏‡•á ‡§ï‡§Æ" : "Below 18"}
      </option>
      <option value="18-35">18 ‚Äì 35</option>
      <option value="36-59">36 ‚Äì 59</option>
      <option value="60+">
        {lang === "te" ? "60 ‡∞™‡±à‡∞¨‡∞°‡∞ø‡∞® ‡∞µ‡∞æ‡∞∞‡±Å" : lang === "hi" ? "60 ‡§∏‡•á ‡§ä‡§™‡§∞" : "60+"}
      </option>
    </>
  )}

  {/* OCCUPATION */}
  {q.key === "occupation" && (
    <>
      <option value="student">
        {lang === "te" ? "‡∞µ‡∞ø‡∞¶‡±ç‡∞Ø‡∞æ‡∞∞‡±ç‡∞•‡∞ø" : lang === "hi" ? "‡§õ‡§æ‡§§‡•ç‡§∞" : "Student"}
      </option>
      <option value="farmer">
        {lang === "te" ? "‡∞∞‡±à‡∞§‡±Å" : lang === "hi" ? "‡§ï‡§ø‡§∏‡§æ‡§®" : "Farmer"}
      </option>
      <option value="labourer">
        {lang === "te" ? "‡∞ï‡±Ç‡∞≤‡±Ä" : lang === "hi" ? "‡§Æ‡§ú‡§º‡§¶‡•Ç‡§∞" : "Labourer"}
      </option>
      <option value="elderly">
        {lang === "te" ? "‡∞µ‡±É‡∞¶‡±ç‡∞ß‡±Å‡∞°‡±Å" : lang === "hi" ? "‡§µ‡•É‡§¶‡•ç‡§ß" : "Elderly"}
      </option>
      <option value="unemployed">
        {lang === "te"
          ? "‡∞®‡∞ø‡∞∞‡±Å‡∞¶‡±ç‡∞Ø‡±ã‡∞ó‡∞ø / ‡∞™‡±á‡∞¶"
          : lang === "hi"
          ? "‡§¨‡•á‡§∞‡•ã‡§ú‡§º‡§ó‡§æ‡§∞ / ‡§ó‡§∞‡•Ä‡§¨"
          : "Unemployed / Poor"}
      </option>
    </>
  )}

  {/* AREA */}
  {q.key === "area" && (
    <>
      <option value="rural">
        {lang === "te" ? "‡∞ó‡±ç‡∞∞‡∞æ‡∞Æ‡±Ä‡∞£" : lang === "hi" ? "‡§ó‡•ç‡§∞‡§æ‡§Æ‡•Ä‡§£" : "Rural"}
      </option>
      <option value="urban">
        {lang === "te" ? "‡∞®‡∞ó‡∞∞" : lang === "hi" ? "‡§∂‡§π‡§∞‡•Ä" : "Urban"}
      </option>
    </>
  )}

  {/* INCOME */}
  {q.key === "income" && (
    <>
      <option value="low">
        {lang === "te"
          ? "‚Çπ2.5 ‡∞≤‡∞ï‡±ç‡∞∑‡∞≤ ‡∞≤‡±ã‡∞™‡±Å"
          : lang === "hi"
          ? "‚Çπ2.5 ‡§≤‡§æ‡§ñ ‡§∏‡•á ‡§ï‡§Æ"
          : "Below ‚Çπ2.5 Lakh"}
      </option>
      <option value="mid">
        {lang === "te"
          ? "‚Çπ2.5 ‚Äì 5 ‡∞≤‡∞ï‡±ç‡∞∑‡∞≤‡±Å"
          : lang === "hi"
          ? "‚Çπ2.5 ‚Äì 5 ‡§≤‡§æ‡§ñ"
          : "‚Çπ2.5 ‚Äì 5 Lakh"}
      </option>
      <option value="high">
        {lang === "te"
          ? "‚Çπ5 ‡∞≤‡∞ï‡±ç‡∞∑‡∞≤‡∞ï‡±Å ‡∞™‡±à‡∞ó‡∞æ"
          : lang === "hi"
          ? "‚Çπ5 ‡§≤‡§æ‡§ñ ‡§∏‡•á ‡§Ö‡§ß‡§ø‡§ï"
          : "Above ‚Çπ5 Lakh"}
      </option>
    </>
  )}

  {/* DISABILITY */}
  {q.key === "disability" && (
    <>
      <option value="yes">
        {lang === "te" ? "‡∞Ö‡∞µ‡±Å‡∞®‡±Å" : lang === "hi" ? "‡§π‡§æ‡§Å" : "Yes"}
      </option>
      <option value="no">
        {lang === "te" ? "‡∞ï‡∞æ‡∞¶‡±Å" : lang === "hi" ? "‡§®‡§π‡•Ä‡§Ç" : "No"}
      </option>
    </>
  )}
</select>
        

          <button className="voice-btn" onClick={startListening}>
            üé§ {TEXT.speak[lang]}
          </button>

          <div className="nav">
            <button onClick={() => (qIndex === 0 ? setStep(0) : setQIndex(qIndex - 1))}>
              {TEXT.back[lang]}
            </button>

            <button
              className="primary"
              onClick={() =>
                qIndex < QUESTIONS.length - 1 ? setQIndex(qIndex + 1) : setStep(2)
              }
            >
              {TEXT.next[lang]}
            </button>
          </div>
        </div>
      </div>
    );
  }
 


  /* ---------- RESULTS ---------- */
  return (
    <div className="results-page">
      <h2>{TEXT.results[lang]}</h2>

      <div className="scheme-row">
        {eligibleSchemes.map((s) => (
          <div className="scheme-card" key={s.id}>
            <img src={schemeImages[s.image] || defaultImg} alt={s.name[lang]} />
            <h3>{s.name[lang]}</h3>
            <p>{s.benefit[lang]}</p>
            <a href={s.officialLink} target="_blank" rel="noreferrer">
              Official Website
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}
