import React, { useState, useEffect } from "react";
import { schemes } from "./data";

// Background images
import bgDefault from "./assets/backgrounds/bg-default.jpg";
import bgFarmer from "./assets/backgrounds/bg-farmer.jpg";
import bgWomen from "./assets/backgrounds/bg-women.jpg";
import bgElderly from "./assets/backgrounds/bg-elderly.jpg";
import bgFamily from "./assets/backgrounds/bg-family.jpg";

// Speech Recognition
const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;

export default function App() {
  const [step, setStep] = useState(0); // 0-home, 1-form, 2-results
  const [lang, setLang] = useState("en-IN");
  const [bg, setBg] = useState(bgDefault);

  const [user, setUser] = useState({
    name: "",
    age: "",
    gender: "",
    occupation: "",
    income: "",
    disability: ""
  });

  /* ========== VOICE OUTPUT ========== */
  const speak = (text) => {
    if (!window.speechSynthesis) return;
    const msg = new SpeechSynthesisUtterance(text);
    msg.lang = lang;
    msg.rate = 0.95;
    speechSynthesis.cancel();
    speechSynthesis.speak(msg);
  };

  /* ========== VOICE INPUT ========== */
  const listen = (field) => {
    if (!SpeechRecognition) return;
    const r = new SpeechRecognition();
    r.lang = lang;
    r.start();

    r.onresult = (e) => {
      const t = e.results[0][0].transcript.toLowerCase();

      if (field === "occupation") {
        if (t.includes("farmer")) setUser({ ...user, occupation: "farmer" });
        else if (t.includes("student")) setUser({ ...user, occupation: "student" });
        else if (t.includes("elder")) setUser({ ...user, occupation: "elderly" });
        else if (t.includes("home")) setUser({ ...user, occupation: "homemaker" });
        else setUser({ ...user, occupation: "poor" });
      }
    };
  };

  /* ========== BACKGROUND SLIDESHOW (HOME) ========== */
  useEffect(() => {
    if (step === 0) {
      const imgs = [bgDefault, bgFarmer, bgWomen, bgElderly, bgFamily];
      let i = 0;
      const interval = setInterval(() => {
        i = (i + 1) % imgs.length;
        setBg(imgs[i]);
      }, 4000);
      return () => clearInterval(interval);
    }
  }, [step]);

  /* ========== OCCUPATION BACKGROUND ========== */
  const setOccupationBg = (occ) => {
    if (occ === "farmer") setBg(bgFarmer);
    else if (occ === "homemaker") setBg(bgWomen);
    else if (occ === "elderly") setBg(bgElderly);
    else setBg(bgFamily);
  };

  /* ========== ELIGIBILITY ========== */
  const eligible = schemes.filter(
    (s) =>
      s.occupation.includes(user.occupation) &&
      (s.income === "any" || s.income === user.income)
  );

  /* ========== VOICE ON RESULTS ========== */
  useEffect(() => {
    if (step === 2 && eligible.length > 0) {
      speak("Eligible government schemes are");
      eligible.forEach((s) => speak(s.name));
    }
  }, [step]);

  /* ================= HOME PAGE ================= */
  if (step === 0) {
    return (
      <div className="hero" style={{ backgroundImage: `url(${bg})` }}>
        <div className="card">
          <h1>Welfare Voice Assistant</h1>

          <input
            placeholder="Enter your name"
            value={user.name}
            onChange={(e) => setUser({ ...user, name: e.target.value })}
          />

          <select value={lang} onChange={(e) => setLang(e.target.value)}>
            <option value="en-IN">English</option>
            <option value="te-IN">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            <option value="hi-IN">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
          </select>

          <button
            className="primary"
            onClick={() => {
              speak(`Welcome ${user.name}. Please answer a few questions.`);
              setStep(1);
            }}
          >
            Continue
          </button>
        </div>
      </div>
    );
  }

  /* ================= FORM PAGE ================= */
  if (step === 1) {
    return (
      <div className="page">
        <input
          placeholder="Age"
          onChange={(e) => setUser({ ...user, age: e.target.value })}
        />

        <select onChange={(e) => setUser({ ...user, gender: e.target.value })}>
          <option>Gender</option>
          <option>Male</option>
          <option>Female</option>
          <option>Other</option>
        </select>

        <select
          onChange={(e) => {
            setUser({ ...user, occupation: e.target.value });
            setOccupationBg(e.target.value);
          }}
        >
          <option>Occupation</option>
          <option value="farmer">Farmer</option>
          <option value="student">Student</option>
          <option value="homemaker">Homemaker</option>
          <option value="elderly">Elderly</option>
          <option value="poor">Unemployed / Poor</option>
        </select>

        <button onClick={() => listen("occupation")}>
          üéôÔ∏è Speak Occupation
        </button>

        <select onChange={(e) => setUser({ ...user, income: e.target.value })}>
          <option>Income</option>
          <option value="low">Below ‚Çπ2.5 Lakh</option>
          <option value="mid">‚Çπ2.5 ‚Äì 5 Lakh</option>
          <option value="high">Above ‚Çπ5 Lakh</option>
        </select>

        <select onChange={(e) => setUser({ ...user, disability: e.target.value })}>
          <option>Disability</option>
          <option value="yes">Yes</option>
          <option value="no">No</option>
        </select>

        <button className="primary" onClick={() => setStep(2)}>
          Check Eligible Schemes
        </button>

        <button onClick={() => setStep(0)}>Go Back</button>
      </div>
    );
  }

  /* ================= RESULTS PAGE ================= */
  return (
    <div className="page">
      <h2>Eligible Government Schemes</h2>

      {eligible.length === 0 && <p>No schemes found.</p>}

      {eligible.map((s) => (
        <div className="scheme" key={s.id}>
          <img
            src={s.image}
            alt={s.name}
            onError={(e) => (e.target.src = "assets/schemes/default.jpg")}
          />
          <h3>{s.name}</h3>
          <p>{s.benefit}</p>
          <span className="tag">{s.government}</span>
          <small>Up-to-date as per official portals</small>
        </div>
      ))}

      <button onClick={() => setStep(1)}>Go Back</button>
    </div>
  );
}
