import React, { useState, useEffect } from "react";
import { schemes } from "./data";

// Background images
import bgDefault from "./assets/backgrounds/bg-default.jpg";
import bgFarmer from "./assets/backgrounds/bg-farmer.jpg";
import bgWomen from "./assets/backgrounds/bg-women.jpg";
import bgElderly from "./assets/backgrounds/bg-elderly.jpg";
import bgFamily from "./assets/backgrounds/bg-family.jpg";

// Speech Recognition setup
const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;

export default function App() {
  const [step, setStep] = useState(0); // 0-home, 1-form, 2-results
  const [lang, setLang] = useState("en-IN");
  const [bg, setBg] = useState(bgDefault);

  const [user, setUser] = useState({
    name: "",
    age: "",
    gender: "",
    occupation: "",
    income: "",
    disability: ""
  });

  /* ================= VOICE OUTPUT ================= */
  const speak = (text) => {
    if (!window.speechSynthesis) return;
    const msg = new SpeechSynthesisUtterance(text);
    msg.lang = lang;
    msg.rate = 0.95;
    speechSynthesis.cancel();
    speechSynthesis.speak(msg);
  };

  /* ================= VOICE INPUT ================= */
  const listen = (field) => {
    if (!SpeechRecognition) {
      alert("Voice input not supported in this browser");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = lang;
    recognition.start();

    recognition.onresult = (event) => {
      const spoken = event.results[0][0].transcript.toLowerCase();

      if (field === "gender") {
        if (spoken.includes("male")) setUser({ ...user, gender: "Male" });
        else if (spoken.includes("female")) setUser({ ...user, gender: "Female" });
        else setUser({ ...user, gender: "Other" });
      }

      if (field === "occupation") {
        if (spoken.includes("farmer")) setUser({ ...user, occupation: "farmer" });
        else if (spoken.includes("student")) setUser({ ...user, occupation: "student" });
        else if (spoken.includes("elder")) setUser({ ...user, occupation: "elderly" });
        else if (spoken.includes("home")) setUser({ ...user, occupation: "homemaker" });
        else setUser({ ...user, occupation: "poor" });
      }

      if (field === "income") {
        if (spoken.includes("low")) setUser({ ...user, income: "low" });
        else if (spoken.includes("middle")) setUser({ ...user, income: "mid" });
        else setUser({ ...user, income: "high" });
      }

      if (field === "disability") {
        setUser({ ...user, disability: spoken.includes("yes") ? "yes" : "no" });
      }
    };
  };

  /* ================= BACKGROUND SLIDESHOW (HOME) ================= */
  useEffect(() => {
    if (step === 0) {
      const images = [bgDefault, bgFarmer, bgWomen, bgElderly, bgFamily];
      let i = 0;
      const interval = setInterval(() => {
        i = (i + 1) % images.length;
        setBg(images[i]);
      }, 4000);
      return () => clearInterval(interval);
    }
  }, [step]);

  /* ================= OCCUPATION BASED BACKGROUND ================= */
  const setOccupationBg = (occ) => {
    if (occ === "farmer") setBg(bgFarmer);
    else if (occ === "homemaker") setBg(bgWomen);
    else if (occ === "elderly") setBg(bgElderly);
    else setBg(bgFamily); // student, poor, unemployed
  };

  /* ================= ELIGIBILITY FILTER ================= */
  const eligible = schemes.filter(
    (s) =>
      s.occupation.includes(user.occupation) &&
      (s.income === "any" || s.income === user.income)
  );

  /* ================= VOICE ON RESULTS PAGE ================= */
  useEffect(() => {
    if (step === 2 && eligible.length > 0) {
      speak("Eligible government schemes are");
      eligible.forEach((s) => speak(s.name));
    }
  }, [step]);

  /* ================= STEP 0 ‚Äì HOME ================= */
  if (step === 0) {
    return (
      <div className="hero" style={{ backgroundImage: `url(${bg})` }}>
        <div className="card">
          <h1>Welfare Voice Assistant</h1>

          <input
            placeholder="Enter your name"
            onChange={(e) => setUser({ ...user, name: e.target.value })}
          />

          <select onChange={(e) => setLang(e.target.value)}>
            <option value="en-IN">English</option>
            <option value="te-IN">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            <option value="hi-IN">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
          </select>

          <button
            onClick={() => {
              speak(`Welcome ${user.name}. Please answer a few questions.`);
              setStep(1);
            }}
          >
            Continue
          </button>
        </div>
      </div>
    );
  }

  /* ================= STEP 1 ‚Äì FORM ================= */
  if (step === 1) {
    return (
      <div className="page">
        <input
          placeholder="Age"
          onChange={(e) => setUser({ ...user, age: e.target.value })}
        />

        <select onChange={(e) => setUser({ ...user, gender: e.target.value })}>
          <option>Gender</option>
          <option>Male</option>
          <option>Female</option>
          <option>Other</option>
        </select>
        <button onClick={() => listen("gender")}>üéôÔ∏è Speak Gender</button>

        <select
          onChange={(e) => {
            setUser({ ...user, occupation: e.target.value });
            setOccupationBg(e.target.value);
          }}
        >
          <option>Occupation</option>
          <option value="farmer">Farmer</option>
          <option value="student">Student</option>
          <option value="homemaker">Homemaker</option>
          <option value="elderly">Elderly</option>
          <option value="poor">Unemployed / Poor</option>
        </select>
        <button onClick={() => listen("occupation")}>üéôÔ∏è Speak Occupation</button>

        <select onChange={(e) => setUser({ ...user, income: e.target.value })}>
          <option>Income</option>
          <option value="low">Below ‚Çπ2.5 Lakh</option>
          <option value="mid">‚Çπ2.5‚Äì5 Lakh</option>
          <option value="high">Above ‚Çπ5 Lakh</option>
        </select>
        <button onClick={() => listen("income")}>üéôÔ∏è Speak Income</button>

        <select onChange={(e) => setUser({ ...user, disability: e.target.value })}>
          <option>Disability</option>
          <option value="yes">Yes</option>
          <option value="no">No</option>
        </select>
        <button onClick={() => listen("disability")}>üéôÔ∏è Speak Disability</button>

        <button onClick={() => setStep(2)}>Check Eligible Schemes</button>
        <button onClick={() => setStep(0)}>Go Back</button>
      </div>
    );
  }

  /* ================= STEP 2 ‚Äì RESULTS ================= */
  return (
    <div className="page">
      <h2>Eligible Government Schemes</h2>

      {eligible.length === 0 && <p>No schemes found for given details.</p>}

      {eligible.map((s) => (
        <div className="scheme" key={s.id}>
          <img
            src={s.image}
            alt={s.name}
            onError={(e) => {
              e.target.src = "assets/schemes/default.jpg";
            }}
          />
          <h3>{s.name}</h3>
          <p>{s.benefit}</p>
          <span className="tag">{s.government}</span>
          <small>Up-to-date as per official portals</small>
        </div>
      ))}

      <button onClick={() => setStep(1)}>Go Back</button>
    </div>
  );
}
