import React, { useState, useEffect } from "react";
import { schemes } from "./data";

// backgrounds
import bgDefault from "./assets/backgrounds/bg-default.jpg";

// scheme images
import tsPost from "./assets/schemes/ts-postmatric.jpg";
import nspImg from "./assets/schemes/nsp.jpg";
import aicteImg from "./assets/schemes/aicte.jpg";
import defaultImg from "./assets/schemes/default.jpg";

const schemeImages = {
  "ts-postmatric": tsPost,
  nsp: nspImg,
  aicte: aicteImg
};

const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;

/* üåê LANGUAGE TEXT */
const TEXT = {
  title: { en: "Welfare Voice Assistant", te: "‡∞∏‡∞Ç‡∞ï‡±ç‡∞∑‡±á‡∞Æ ‡∞µ‡∞æ‡∞Ø‡∞ø‡∞∏‡±ç ‡∞Ö‡∞∏‡∞ø‡∞∏‡±ç‡∞ü‡±Ü‡∞Ç‡∞ü‡±ç", hi: "‡§ï‡§≤‡•ç‡§Ø‡§æ‡§£ ‡§µ‡•â‡§á‡§∏ ‡§∏‡§π‡§æ‡§Ø‡§ï" },
  next: { en: "Next", te: "‡∞§‡∞∞‡±ç‡∞µ‡∞æ‡∞§", hi: "‡§Ö‡§ó‡§≤‡§æ" },
  back: { en: "Back", te: "‡∞µ‡±Ü‡∞®‡∞ï‡±ç‡∞ï‡∞ø", hi: "‡§µ‡§æ‡§™‡§∏" },
  speak: { en: "Tap to Speak", te: "‡∞Æ‡∞æ‡∞ü‡±ç‡∞≤‡∞æ‡∞°‡∞Ç‡∞°‡∞ø", hi: "‡§¨‡•ã‡§≤‡•á‡§Ç" },
  results: { en: "Eligible Government Schemes", te: "‡∞Ö‡∞∞‡±ç‡∞π‡∞Æ‡±à‡∞® ‡∞™‡±ç‡∞∞‡∞≠‡±Å‡∞§‡±ç‡∞µ ‡∞™‡∞•‡∞ï‡∞æ‡∞≤‡±Å", hi: "‡§™‡§æ‡§§‡•ç‡§∞ ‡§∏‡§∞‡§ï‡§æ‡§∞‡•Ä ‡§Ø‡•ã‡§ú‡§®‡§æ‡§è‡§Å" },
  more: { en: "More Information", te: "‡∞Æ‡∞∞‡∞ø‡∞®‡±ç‡∞®‡∞ø ‡∞µ‡∞ø‡∞µ‡∞∞‡∞æ‡∞≤‡±Å", hi: "‡§Ö‡§ß‡§ø‡§ï ‡§ú‡§æ‡§®‡§ï‡§æ‡§∞‡•Ä" }
};

/* ‚ùì QUESTIONS CONFIG */
const QUESTIONS = [
  { key: "gender", en: "What is your gender?", te: "‡∞Æ‡±Ä ‡∞≤‡∞ø‡∞Ç‡∞ó‡∞Ç ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§≤‡§ø‡§Ç‡§ó ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "occupation", en: "What is your occupation?", te: "‡∞Æ‡±Ä ‡∞µ‡±É‡∞§‡±ç‡∞§‡∞ø ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§™‡•á‡§∂‡§æ ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "disability", en: "Do you have a disability?", te: "‡∞Æ‡±Ä‡∞ï‡±Å ‡∞µ‡∞ø‡∞ï‡∞≤‡∞æ‡∞Ç‡∞ó‡∞§ ‡∞â‡∞Ç‡∞¶‡∞æ?", hi: "‡§ï‡•ç‡§Ø‡§æ ‡§Ü‡§™‡§ï‡•ã ‡§µ‡§ø‡§ï‡§≤‡§æ‡§Ç‡§ó‡§§‡§æ ‡§π‡•à?" },
  { key: "income", en: "What is your income range?", te: "‡∞Æ‡±Ä ‡∞Ü‡∞¶‡∞æ‡∞Ø‡∞Ç ‡∞é‡∞Ç‡∞§?", hi: "‡§Ü‡§™‡§ï‡•Ä ‡§Ü‡§Ø ‡§ï‡§ø‡§§‡§®‡•Ä ‡§π‡•à?" }
];

export default function App() {
  const [step, setStep] = useState(0);      // 0=home, 1=questions, 2=results
  const [qStep, setQStep] = useState(0);
  const [lang, setLang] = useState("en");

  const [user, setUser] = useState({
    gender: "",
    occupation: "",
    disability: "",
    income: ""
  });

  /* üîä VOICE SPEAK */
  const speak = (text) => {
    if (!window.speechSynthesis) return;
    const msg = new SpeechSynthesisUtterance(text);
    msg.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    speechSynthesis.cancel();
    speechSynthesis.speak(msg);
  };

  /* üé§ VOICE LISTEN */
  const listen = (field) => {
    if (!SpeechRecognition) return;
    const r = new SpeechRecognition();
    r.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    r.start();
    r.onresult = (e) => {
      setUser((prev) => ({ ...prev, [field]: e.results[0][0].transcript.toLowerCase() }));
    };
  };

  /* üó£Ô∏è AUTO SPEAK QUESTION (FIXED) */
  useEffect(() => {
    if (step === 1) {
      const q = QUESTIONS[qStep];
      speak(q[lang]);
    }
  }, [qStep, step]);

  /* üßÆ ELIGIBILITY (FIXED ‚Äì SCHEMES NOW SHOW) */
  const eligible = schemes.filter(
    (s) =>
      s.occupation.includes(user.occupation) &&
      (s.income === "any" || s.income === user.income)
  );

  /* üè† HOME */
  if (step === 0) {
    return (
      <div className="hero" style={{ backgroundImage: `url(${bgDefault})` }}>
        <div className="card">
          <h1>{TEXT.title[lang]}</h1>

          <select onChange={(e) => setLang(e.target.value)}>
            <option value="en">English</option>
            <option value="te">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            <option value="hi">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
          </select>

          <button className="primary" onClick={() => setStep(1)}>
            {TEXT.next[lang]}
          </button>
        </div>
      </div>
    );
  }

  /* üìù QUESTIONS (ONE BY ONE WITH OPTIONS) */
  if (step === 1) {
    const q = QUESTIONS[qStep];

    return (
      <div className="page">
        <div style={{ marginBottom: 12 }}>
          <strong>{qStep + 1} / {QUESTIONS.length}</strong>
          <progress value={qStep + 1} max={QUESTIONS.length} style={{ width: "100%" }} />
        </div>

        <div className="card">
          <h2>{q[lang]}</h2>

          {/* GENDER */}
          {q.key === "gender" && (
            <select onChange={(e) => setUser({ ...user, gender: e.target.value })}>
              <option value="">Select</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
          )}

          {/* OCCUPATION */}
          {q.key === "occupation" && (
            <select onChange={(e) => setUser({ ...user, occupation: e.target.value })}>
              <option value="">Select</option>
              <option value="student">Student</option>
              <option value="farmer">Farmer</option>
              <option value="homemaker">Homemaker</option>
              <option value="elderly">Elderly</option>
              <option value="poor">Unemployed / Poor</option>
            </select>
          )}

          {/* DISABILITY */}
          {q.key === "disability" && (
            <select onChange={(e) => setUser({ ...user, disability: e.target.value })}>
              <option value="">Select</option>
              <option value="yes">Yes</option>
              <option value="no">No</option>
            </select>
          )}

          {/* INCOME */}
          {q.key === "income" && (
            <select onChange={(e) => setUser({ ...user, income: e.target.value })}>
              <option value="">Select</option>
              <option value="low">Below ‚Çπ2.5 Lakh</option>
              <option value="mid">‚Çπ2.5‚Äì5 Lakh</option>
              <option value="high">Above ‚Çπ5 Lakh</option>
            </select>
          )}

          <button onClick={() => listen(q.key)}>
            üé§ {TEXT.speak[lang]}
          </button>
        </div>

        {/* NAVIGATION */}
        <div style={{ display: "flex", gap: "10px", marginTop: "16px" }}>
          <button
            onClick={() => qStep > 0 ? setQStep(qStep - 1) : setStep(0)}
          >
            {TEXT.back[lang]}
          </button>

          <button
            className="primary"
            onClick={() =>
              qStep < QUESTIONS.length - 1 ? setQStep(qStep + 1) : setStep(2)
            }
          >
            {TEXT.next[lang]}
          </button>
        </div>
      </div>
    );
  }

  /* üìä RESULTS */
  return (
    <div className="page">
      <h2>{TEXT.results[lang]}</h2>

      {eligible.map((s) => (
        <div className="scheme" key={s.id}>
          <img src={schemeImages[s.image] || defaultImg} alt="" />
          <h3>{s.name[lang]}</h3>
          <p>{s.benefit[lang]}</p>

          <a
            href={s.officialLink}
            target="_blank"
            rel="noreferrer"
            className="info-btn"
          >
            {TEXT.more[lang]}
          </a>
        </div>
      ))}
    </div>
  );
}
