import React, { useState, useEffect } from "react";
import "./style.css";
import { schemes } from "./data";

// background
import bgDefault from "./assets/backgrounds/bg-default.jpg";

// scheme images
import tsPost from "./assets/schemes/ts-postmatric.jpg";
import nspImg from "./assets/schemes/nsp.jpg";
import aicteImg from "./assets/schemes/aicte.jpg";
import pmkisanImg from "./assets/schemes/pmkisan.jpg";
import rythuImg from "./assets/schemes/rythubharosa.jpg";
import defaultImg from "./assets/schemes/default.jpg";

const schemeImages = {
  "ts-postmatric": tsPost,
  "nsp": nspImg,
  "aicte": aicteImg,
  "pmkisan": pmkisanImg,
  "rythubharosa": rythuImg
};

const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;

/* üåê TEXT */
const TEXT = {
  title: { en: "Welfare Voice Assistant", te: "‡∞∏‡∞Ç‡∞ï‡±ç‡∞∑‡±á‡∞Æ ‡∞µ‡∞æ‡∞Ø‡∞ø‡∞∏‡±ç ‡∞Ö‡∞∏‡∞ø‡∞∏‡±ç‡∞ü‡±Ü‡∞Ç‡∞ü‡±ç", hi: "‡§ï‡§≤‡•ç‡§Ø‡§æ‡§£ ‡§µ‡•â‡§á‡§∏ ‡§∏‡§π‡§æ‡§Ø‡§ï" },
  next: { en: "Next", te: "‡∞§‡∞∞‡±ç‡∞µ‡∞æ‡∞§", hi: "‡§Ö‡§ó‡§≤‡§æ" },
  back: { en: "Back", te: "‡∞µ‡±Ü‡∞®‡∞ï‡±ç‡∞ï‡∞ø", hi: "‡§µ‡§æ‡§™‡§∏" },
  speak: { en: "Tap to Speak", te: "‡∞Æ‡∞æ‡∞ü‡±ç‡∞≤‡∞æ‡∞°‡∞Ç‡∞°‡∞ø", hi: "‡§¨‡•ã‡§≤‡•á‡§Ç" },
  results: { en: "Eligible Government Schemes", te: "‡∞Ö‡∞∞‡±ç‡∞π‡∞Æ‡±à‡∞® ‡∞™‡±ç‡∞∞‡∞≠‡±Å‡∞§‡±ç‡∞µ ‡∞™‡∞•‡∞ï‡∞æ‡∞≤‡±Å", hi: "‡§™‡§æ‡§§‡•ç‡§∞ ‡§∏‡§∞‡§ï‡§æ‡§∞‡•Ä ‡§Ø‡•ã‡§ú‡§®‡§æ‡§è‡§Å" },
  more: { en: "More Information", te: "‡∞Æ‡∞∞‡∞ø‡∞®‡±ç‡∞®‡∞ø ‡∞µ‡∞ø‡∞µ‡∞∞‡∞æ‡∞≤‡±Å", hi: "‡§Ö‡§ß‡§ø‡§ï ‡§ú‡§æ‡§®‡§ï‡§æ‡§∞‡•Ä" }
};

/* ‚ùì QUESTIONS (AGE ADDED) */
const QUESTIONS = [
  { key: "gender", en: "What is your gender?", te: "‡∞Æ‡±Ä ‡∞≤‡∞ø‡∞Ç‡∞ó‡∞Ç ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§≤‡§ø‡§Ç‡§ó ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "age", en: "What is your age?", te: "‡∞Æ‡±Ä ‡∞µ‡∞Ø‡∞∏‡±ç‡∞∏‡±Å ‡∞é‡∞Ç‡∞§?", hi: "‡§Ü‡§™‡§ï‡•Ä ‡§â‡§Æ‡•ç‡§∞ ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "occupation", en: "What is your occupation?", te: "‡∞Æ‡±Ä ‡∞µ‡±É‡∞§‡±ç‡∞§‡∞ø ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§™‡•á‡§∂‡§æ ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "disability", en: "Do you have a disability?", te: "‡∞Æ‡±Ä‡∞ï‡±Å ‡∞µ‡∞ø‡∞ï‡∞≤‡∞æ‡∞Ç‡∞ó‡∞§ ‡∞â‡∞Ç‡∞¶‡∞æ?", hi: "‡§ï‡•ç‡§Ø‡§æ ‡§Ü‡§™‡§ï‡•ã ‡§µ‡§ø‡§ï‡§≤‡§æ‡§Ç‡§ó‡§§‡§æ ‡§π‡•à?" },
  { key: "income", en: "What is your income range?", te: "‡∞Æ‡±Ä ‡∞Ü‡∞¶‡∞æ‡∞Ø‡∞Ç ‡∞é‡∞Ç‡∞§?", hi: "‡§Ü‡§™‡§ï‡•Ä ‡§Ü‡§Ø ‡§ï‡§ø‡§§‡§®‡•Ä ‡§π‡•à?" }
];

export default function App() {
  const [step, setStep] = useState(0);
  const [qStep, setQStep] = useState(0);
  const [lang, setLang] = useState("en");

  const [user, setUser] = useState({
    gender: "",
    age: "",
    occupation: "",
    disability: "",
    income: ""
  });

  /* üîä SPEAK */
  const speak = (text) => {
    if (!window.speechSynthesis) return;
    window.speechSynthesis.cancel();
    const msg = new SpeechSynthesisUtterance(text);
    msg.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    msg.rate = 0.9;
    window.speechSynthesis.speak(msg);
  };

  /* üé§ LISTEN */
  const listen = (field) => {
    if (!SpeechRecognition) return;
    const r = new SpeechRecognition();
    r.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    r.start();

    r.onresult = (e) => {
      const spoken = e.results[0][0].transcript.toLowerCase();
      const number = spoken.match(/\d+/);

      const value =
        field === "age" && number ? Number(number[0]) :
        spoken.includes("farmer") ? "farmer" :
        spoken.includes("student") ? "student" :
        spoken.includes("female") ? "female" :
        spoken.includes("male") ? "male" :
        spoken.includes("yes") ? "yes" :
        spoken.includes("no") ? "no" :
        spoken;

      setUser((prev) => ({ ...prev, [field]: value }));
    };
  };

  /* üîÅ AUTO SPEAK QUESTIONS */
  useEffect(() => {
    if (step === 1) {
      speak(QUESTIONS[qStep][lang]);
    }
  }, [qStep, step, lang]);

  /* üßÆ ELIGIBILITY (AGE INCLUDED) */
  const eligible = schemes.filter((s) => {
    const occ = s.occupation.includes(user.occupation);
    const inc = s.income === "any" || s.income === user.income;
    const dis = s.disability === "any" || s.disability === user.disability;
    const ageMatch =
      user.age &&
      user.age >= s.minAge &&
      user.age <= s.maxAge;

    return occ && inc && dis && ageMatch;
  });

  const getSchemeImage = (id) => schemeImages[id] || defaultImg;

  /* üè† HOME */
  if (step === 0) {
    return (
      <div className="hero" style={{ backgroundImage: `url(${bgDefault})` }}>
        <div className="card">
          <h1>{TEXT.title[lang]}</h1>

          <select value={lang} onChange={(e) => setLang(e.target.value)}>
            <option value="en">English</option>
            <option value="te">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            <option value="hi">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
          </select>

          <button className="primary" onClick={() => setStep(1)}>
            {TEXT.next[lang]}
          </button>
        </div>
      </div>
    );
  }

  /* üìù QUESTIONS */
  if (step === 1) {
    const q = QUESTIONS[qStep];

    return (
      <div className="page">
        <div className="card">
          <h2>{q[lang]}</h2>

          {q.key === "gender" && (
            <select onChange={(e) => setUser({ ...user, gender: e.target.value })}>
              <option value="">Select</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
            </select>
          )}

          {q.key === "age" && (
            <input
              type="number"
              placeholder="Enter your age"
              onChange={(e) =>
                setUser({ ...user, age: Number(e.target.value) })
              }
            />
          )}

          {q.key === "occupation" && (
            <select onChange={(e) => setUser({ ...user, occupation: e.target.value })}>
              <option value="">Select</option>
              <option value="student">Student</option>
              <option value="farmer">Farmer</option>
            </select>
          )}

          {q.key === "disability" && (
            <select onChange={(e) => setUser({ ...user, disability: e.target.value })}>
              <option value="">Select</option>
              <option value="yes">Yes</option>
              <option value="no">No</option>
            </select>
          )}

          {q.key === "income" && (
            <select onChange={(e) => setUser({ ...user, income: e.target.value })}>
              <option value="">Select</option>
              <option value="low">Below ‚Çπ2.5 Lakh</option>
              <option value="mid">‚Çπ2.5‚Äì5 Lakh</option>
              <option value="high">Above ‚Çπ5 Lakh</option>
            </select>
          )}

          <button onClick={() => listen(q.key)}>üé§ {TEXT.speak[lang]}</button>
        </div>

        <div className="nav">
          <button onClick={() => qStep > 0 ? setQStep(qStep - 1) : setStep(0)}>
            {TEXT.back[lang]}
          </button>

          <button
            className="primary"
            onClick={() =>
              qStep < QUESTIONS.length - 1
                ? setQStep(qStep + 1)
                : setStep(2)
            }
          >
            {TEXT.next[lang]}
          </button>
        </div>
      </div>
    );
  }

  /* üìä RESULTS */
  return (
    <div className="page">
      <h2>{TEXT.results[lang]}</h2>

      {eligible.map((s) => (
        <div className="scheme" key={s.id}>
          <img src={getSchemeImage(s.id)} alt={s.name[lang]} />
          <h3>{s.name[lang]}</h3>
          <p>{s.benefit[lang]}</p>

          <button
            className="voice-btn"
            onClick={() => speak(`${s.name[lang]}. ${s.benefit[lang]}`)}
          >
            üîä Hear Scheme
          </button>

          <a
            href={s.officialLink}
            target="_blank"
            rel="noreferrer"
            className="info-btn"
          >
            {TEXT.more[lang]}
          </a>
        </div>
      ))}
    </div>
  );
}
