import React, { useState, useEffect } from "react";
import { schemes } from "./data";

// scheme images
import tsPost from "./assets/schemes/ts-postmatric.jpg";
import nspImg from "./assets/schemes/nsp.jpg";
import aicteImg from "./assets/schemes/aicte.jpg";
import defaultImg from "./assets/schemes/default.jpg";

// backgrounds
import bgDefault from "./assets/backgrounds/bg-default.jpg";

const schemeImages = {
  "ts-postmatric": tsPost,
  "nsp": nspImg,
  "aicte": aicteImg
};

const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;

/* üåê UI TEXT (MULTILINGUAL) */
const TEXT = {
  title: {
    en: "Welfare Voice Assistant",
    te: "‡∞∏‡∞Ç‡∞ï‡±ç‡∞∑‡±á‡∞Æ ‡∞µ‡∞æ‡∞Ø‡∞ø‡∞∏‡±ç ‡∞Ö‡∞∏‡∞ø‡∞∏‡±ç‡∞ü‡±Ü‡∞Ç‡∞ü‡±ç",
    hi: "‡§ï‡§≤‡•ç‡§Ø‡§æ‡§£ ‡§µ‡•â‡§á‡§∏ ‡§∏‡§π‡§æ‡§Ø‡§ï"
  },
  name: {
    en: "Enter your name",
    te: "‡∞Æ‡±Ä ‡∞™‡±á‡∞∞‡±Å ‡∞®‡∞Æ‡±ã‡∞¶‡±Å ‡∞ö‡±á‡∞Ø‡∞Ç‡∞°‡∞ø",
    hi: "‡§Ö‡§™‡§®‡§æ ‡§®‡§æ‡§Æ ‡§¶‡§∞‡•ç‡§ú ‡§ï‡§∞‡•á‡§Ç"
  },
  continue: {
    en: "Continue",
    te: "‡∞ï‡±ä‡∞®‡∞∏‡∞æ‡∞ó‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø",
    hi: "‡§ú‡§æ‡§∞‡•Ä ‡§∞‡§ñ‡•á‡§Ç"
  },
  age: {
    en: "Age",
    te: "‡∞µ‡∞Ø‡∞∏‡±ç‡∞∏‡±Å",
    hi: "‡§Ü‡§Ø‡•Å"
  },
  occupation: {
    en: "Occupation",
    te: "‡∞µ‡±É‡∞§‡±ç‡∞§‡∞ø",
    hi: "‡§™‡•á‡§∂‡§æ"
  },
  income: {
    en: "Income",
    te: "‡∞Ü‡∞¶‡∞æ‡∞Ø‡∞Ç",
    hi: "‡§Ü‡§Ø"
  },
  check: {
    en: "Check Eligible Schemes",
    te: "‡∞Ö‡∞∞‡±ç‡∞π‡∞Æ‡±à‡∞® ‡∞™‡∞•‡∞ï‡∞æ‡∞≤‡∞®‡±Å ‡∞ö‡±Ç‡∞°‡∞Ç‡∞°‡∞ø",
    hi: "‡§™‡§æ‡§§‡•ç‡§∞ ‡§Ø‡•ã‡§ú‡§®‡§æ‡§è‡§Å ‡§¶‡•á‡§ñ‡•á‡§Ç"
  },
  results: {
    en: "Eligible Government Schemes",
    te: "‡∞Ö‡∞∞‡±ç‡∞π‡∞Æ‡±à‡∞® ‡∞™‡±ç‡∞∞‡∞≠‡±Å‡∞§‡±ç‡∞µ ‡∞™‡∞•‡∞ï‡∞æ‡∞≤‡±Å",
    hi: "‡§™‡§æ‡§§‡•ç‡§∞ ‡§∏‡§∞‡§ï‡§æ‡§∞‡•Ä ‡§Ø‡•ã‡§ú‡§®‡§æ‡§è‡§Å"
  },
  more: {
    en: "More Information",
    te: "‡∞Æ‡∞∞‡∞ø‡∞®‡±ç‡∞®‡∞ø ‡∞µ‡∞ø‡∞µ‡∞∞‡∞æ‡∞≤‡±Å",
    hi: "‡§Ö‡§ß‡§ø‡§ï ‡§ú‡§æ‡§®‡§ï‡§æ‡§∞‡•Ä"
  }
};

export default function App() {
  const [lang, setLang] = useState("en");
  const [step, setStep] = useState(0);
  const [user, setUser] = useState({ occupation: "", income: "low" });

  /* üîä VOICE */
  const speak = (text) => {
    const msg = new SpeechSynthesisUtterance(text);
    msg.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    speechSynthesis.cancel();
    speechSynthesis.speak(msg);
  };

  /* üßÆ ELIGIBILITY */
  const eligible = schemes.filter(
    (s) =>
      s.occupation.includes(user.occupation) &&
      (s.income === "any" || s.income === user.income)
  );

  useEffect(() => {
    if (step === 2 && eligible.length > 0) {
      speak(TEXT.results[lang]);
      eligible.forEach((s) => speak(s.name[lang]));
    }
  }, [step]);

  /* üè† HOME */
  if (step === 0) {
    return (
      <div className="hero" style={{ backgroundImage: `url(${bgDefault})` }}>
        <div className="card">
          <h1>{TEXT.title[lang]}</h1>

          <input placeholder={TEXT.name[lang]} />

          <select onChange={(e) => setLang(e.target.value)}>
            <option value="en">English</option>
            <option value="te">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            <option value="hi">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
          </select>

          <button
            className="primary"
            onClick={() => {
              speak(TEXT.continue[lang]);
              setStep(1);
            }}
          >
            {TEXT.continue[lang]}
          </button>
        </div>
      </div>
    );
  }

  /* üìù QUESTIONS */
  if (step === 1) {
    return (
      <div className="page">
        <select onChange={(e) => setUser({ ...user, occupation: e.target.value })}>
          <option>{TEXT.occupation[lang]}</option>
          <option value="student">
            {lang === "te" ? "‡∞µ‡∞ø‡∞¶‡±ç‡∞Ø‡∞æ‡∞∞‡±ç‡∞•‡∞ø" : lang === "hi" ? "‡§õ‡§æ‡§§‡•ç‡§∞" : "Student"}
          </option>
        </select>

        <select onChange={(e) => setUser({ ...user, income: e.target.value })}>
          <option>{TEXT.income[lang]}</option>
          <option value="low">
            {lang === "te" ? "‡∞§‡∞ï‡±ç‡∞ï‡±Å‡∞µ" : lang === "hi" ? "‡§ï‡§Æ" : "Low"}
          </option>
        </select>

        <button className="primary" onClick={() => setStep(2)}>
          {TEXT.check[lang]}
        </button>
      </div>
    );
  }

  /* üìä RESULTS */
  return (
    <div className="page">
      <h2>{TEXT.results[lang]}</h2>

      {eligible.map((s) => (
        <div className="scheme" key={s.id}>
          <img
            src={schemeImages[s.image] || defaultImg}
            alt={s.name[lang]}
          />
          <h3>{s.name[lang]}</h3>
          <p>{s.benefit[lang]}</p>
          <span className="tag">{s.government}</span>

          <a
            className="info-btn"
            href={s.officialLink}
            target="_blank"
            rel="noopener noreferrer"
          >
            {TEXT.more[lang]}
          </a>
        </div>
      ))}
    </div>
  );
}
