};
const [introSpoken, setIntroSpoken] = useState(false);

/* SPEECH RECOGNITION */
const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;

/* TEXT */
const TEXT = {
  title: {
    en: "Welfare Voice Assistant",
    te: "‡∞∏‡∞Ç‡∞ï‡±ç‡∞∑‡±á‡∞Æ ‡∞µ‡∞æ‡∞Ø‡∞ø‡∞∏‡±ç ‡∞Ö‡∞∏‡∞ø‡∞∏‡±ç‡∞ü‡±Ü‡∞Ç‡∞ü‡±ç",
    hi: "‡§ï‡§≤‡•ç‡§Ø‡§æ‡§£ ‡§µ‡•â‡§á‡§∏ ‡§∏‡§π‡§æ‡§Ø‡§ï"
  },
  start: { en: "Start", te: "‡∞™‡±ç‡∞∞‡∞æ‡∞∞‡∞Ç‡∞≠‡∞ø‡∞Ç‡∞ö‡∞Ç‡∞°‡∞ø", hi: "‡§∂‡•Å‡§∞‡•Ç ‡§ï‡§∞‡•á‡§Ç" },
  next: { en: "Next", te: "‡∞§‡∞∞‡±ç‡∞µ‡∞æ‡∞§", hi: "‡§Ö‡§ó‡§≤‡§æ" },
  back: { en: "Back", te: "‡∞µ‡±Ü‡∞®‡∞ï‡±ç‡∞ï‡∞ø", hi: "‡§µ‡§æ‡§™‡§∏" },
  speak: { en: "Tap to Speak", te: "‡∞Æ‡∞æ‡∞ü‡±ç‡∞≤‡∞æ‡∞°‡∞Ç‡∞°‡∞ø", hi: "‡§¨‡•ã‡§≤‡•á‡§Ç" },
  select: { en: "Select", te: "‡∞é‡∞Ç‡∞ö‡±Å‡∞ï‡±ã‡∞Ç‡∞°‡∞ø", hi: "‡§ö‡•Å‡§®‡•á‡§Ç" },
  results: {
    en: "Eligible Government Schemes",
    te: "‡∞Ö‡∞∞‡±ç‡∞π‡∞Æ‡±à‡∞® ‡∞™‡±ç‡∞∞‡∞≠‡±Å‡∞§‡±ç‡∞µ ‡∞™‡∞•‡∞ï‡∞æ‡∞≤‡±Å",
    hi: "‡§™‡§æ‡§§‡•ç‡§∞ ‡§∏‡§∞‡§ï‡§æ‡§∞‡•Ä ‡§Ø‡•ã‡§ú‡§®‡§æ‡§è‡§Å"
  }
};

/* QUESTIONS */
const QUESTIONS = [
  { key: "gender", en: "What is your gender?", te: "‡∞Æ‡±Ä ‡∞≤‡∞ø‡∞Ç‡∞ó‡∞Ç ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§≤‡§ø‡§Ç‡§ó ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "age", en: "What is your age group?", te: "‡∞Æ‡±Ä ‡∞µ‡∞Ø‡∞∏‡±ç‡∞∏‡±Å ‡∞µ‡∞∞‡±ç‡∞ó‡∞Ç?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§Ü‡§Ø‡•Å ‡§µ‡§∞‡•ç‡§ó?" },
  { key: "occupation", en: "What is your occupation?", te: "‡∞Æ‡±Ä ‡∞µ‡±É‡∞§‡±ç‡∞§‡∞ø ‡∞è‡∞Æ‡∞ø‡∞ü‡∞ø?", hi: "‡§Ü‡§™‡§ï‡§æ ‡§™‡•á‡§∂‡§æ ‡§ï‡•ç‡§Ø‡§æ ‡§π‡•à?" },
  { key: "area", en: "Where do you live?", te: "‡∞Æ‡±Ä ‡∞®‡∞ø‡∞µ‡∞æ‡∞∏ ‡∞™‡±ç‡∞∞‡∞æ‡∞Ç‡∞§‡∞Ç?", hi: "‡§Ü‡§™ ‡§ï‡§π‡§æ‡§Å ‡§∞‡§π‡§§‡•á ‡§π‡•à‡§Ç?" },
  { key: "income", en: "What is your income range?", te: "‡∞Æ‡±Ä ‡∞Ü‡∞¶‡∞æ‡∞Ø‡∞Ç ‡∞é‡∞Ç‡∞§?", hi: "‡§Ü‡§™‡§ï‡•Ä ‡§Ü‡§Ø ‡§ï‡§ø‡§§‡§®‡•Ä ‡§π‡•à?" },
  { key: "disability", en: "Do you have a disability?", te: "‡∞Æ‡±Ä‡∞ï‡±Å ‡∞µ‡∞ø‡∞ï‡∞≤‡∞æ‡∞Ç‡∞ó‡∞§ ‡∞â‡∞Ç‡∞¶‡∞æ?", hi: "‡§ï‡•ç‡§Ø‡§æ ‡§Ü‡§™‡§ï‡•ã ‡§µ‡§ø‡§ï‡§≤‡§æ‡§Ç‡§ó‡§§‡§æ ‡§π‡•à?" }
];

export default function App() {
  const [step, setStep] = useState(0);
  const [qStep, setQStep] = useState(0);
  const [lang, setLang] = useState("en");

  const [user, setUser] = useState({
    name: "",
    gender: "",
    age: "",
    occupation: "",
    area: "",
    income: "",
    disability: ""
  });

  /* VOICE STATE */
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [voiceIndex, setVoiceIndex] = useState(0);

  /* SPEAK TEXT */
  const speakText = (text) => {
    if (!window.speechSynthesis) return;

    speechSynthesis.cancel();

    const u = new SpeechSynthesisUtterance(text);
    u.lang = lang === "te" ? "te-IN" : lang === "hi" ? "hi-IN" : "en-IN";
    u.rate = 0.9;

    u.onstart = () => setIsSpeaking(true);
    u.onend = () => setIsSpeaking(false);

    speechSynthesis.speak(u);
  };

  /* STOP VOICE */
  const stopVoice = () => {
    speechSynthesis.cancel();
    setIsSpeaking(false);
    setVoiceIndex(0);
  };

  /* ELIGIBLE SCHEMES */
  const eligible = schemes.filter((s) => {
    const occ = s.occupation.includes("any") || s.occupation.includes(user.occupation);
    const inc = s.income.includes("any") || s.income.includes(user.income);
    const dis = s.disability.includes("any") || s.disability.includes(user.disability);
    return occ && inc && dis;
  });

  /* START SCHEME VOICE (STRUCTURED) */
 const startVoiceForSchemes = () => {
  if (eligible.length === 0) {
    speakText(
      lang === "te"
        ? "‡∞Æ‡±Ä‡∞ï‡±Å ‡∞Ö‡∞∞‡±ç‡∞π‡∞Æ‡±à‡∞® ‡∞™‡∞•‡∞ï‡∞æ‡∞≤‡±Å ‡∞≤‡±á‡∞µ‡±Å"
        : lang === "hi"
        ? "‡§Ü‡§™‡§ï‡•á ‡§≤‡§ø‡§è ‡§ï‡•ã‡§à ‡§™‡§æ‡§§‡•ç‡§∞ ‡§Ø‡•ã‡§ú‡§®‡§æ ‡§®‡§π‡•Ä‡§Ç ‡§π‡•à"
        : "No eligible schemes found"
    );
    return;
  }

  speechSynthesis.cancel();
  setIntroSpoken(false);
  setVoiceIndex(0);
  setIsSpeaking(true);
};

const getResultsIntroText = () => {
  if (lang === "te") {
    return `‡∞Æ‡±Ä ‡∞µ‡∞ø‡∞µ‡∞∞‡∞æ‡∞≤ ‡∞Ü‡∞ß‡∞æ‡∞∞‡∞Ç‡∞ó‡∞æ, ‡∞Æ‡±Ä‡∞∞‡±Å ‡∞Ö‡∞∞‡±ç‡∞π‡∞§ ‡∞™‡±ä‡∞Ç‡∞¶‡∞ø‡∞® ‡∞™‡±ç‡∞∞‡∞≠‡±Å‡∞§‡±ç‡∞µ ‡∞™‡∞•‡∞ï‡∞æ‡∞≤‡±Å ‡∞á‡∞µ‡∞ø. ‡∞í‡∞ï‡±ç‡∞ï‡±ä‡∞ï‡±ç‡∞ï‡∞ü‡∞ø‡∞ó‡∞æ ‡∞µ‡∞ø‡∞µ‡∞∞‡∞ø‡∞∏‡±ç‡∞§‡∞æ‡∞®‡±Å.`;
  }

  if (lang === "hi") {
    return `‡§Ü‡§™‡§ï‡•Ä ‡§ú‡§æ‡§®‡§ï‡§æ‡§∞‡•Ä ‡§ï‡•á ‡§Ü‡§ß‡§æ‡§∞ ‡§™‡§∞, ‡§Ü‡§™ ‡§á‡§® ‡§∏‡§∞‡§ï‡§æ‡§∞‡•Ä ‡§Ø‡•ã‡§ú‡§®‡§æ‡§ì‡§Ç ‡§ï‡•á ‡§≤‡§ø‡§è ‡§™‡§æ‡§§‡•ç‡§∞ ‡§π‡•à‡§Ç‡•§ ‡§Æ‡•à‡§Ç ‡§è‡§ï-‡§è‡§ï ‡§ï‡§∞‡§ï‡•á ‡§∏‡§Æ‡§ù‡§æ‡§ä‡§Å‡§ó‡§æ‡•§`;
  }

  return `Based on your details, you are eligible for the following government schemes. I will explain them one by one.`;
};

  /* READ SCHEMES ONE BY ONE */
 


  /* HOME PAGE */
  if (step === 0) {
    return (
      <div className="hero" style={{ backgroundImage: `url(${bgDefault})` }}>
        <div className="card center">
          <h1>{TEXT.title[lang]}</h1>

          <input
            placeholder="Enter your name"
            value={user.name}
            onChange={(e) => setUser({ ...user, name: e.target.value })}
          />

          <select value={lang} onChange={(e) => setLang(e.target.value)}>
            <option value="en">English</option>
            <option value="te">‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å</option>
            <option value="hi">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
          </select>

          <button className="primary" onClick={() => setStep(1)}>
            {TEXT.start[lang]}
          </button>
        </div>
      </div>
    );
  }

  /* QUESTIONS */
  if (step
 === 1) {
    const q = QUESTIONS[qStep];

    return (
      <div className="page center">
        <div className="card center">
          <div className="step-indicator">
            Step {qStep + 1} / {QUESTIONS.length}
          </div>

          <h2>{q[lang]}</h2>

          <select
  value={user[q.key] || ""}
  onChange={(e) => setUser({ ...user, [q.key]: e.target.value })}
>
  <option value="" disabled>
    {TEXT.select[lang]}
  </option>

  {/* GENDER */}
  {q.key === "gender" && (
    <>
      <option value="male">
        {lang === "te" ? "‡∞™‡±Å‡∞∞‡±Å‡∞∑‡±Å‡∞°‡±Å" : lang === "hi" ? "‡§™‡•Å‡§∞‡•Å‡§∑" : "Male"}
      </option>
      <option value="female">
        {lang === "te" ? "‡∞∏‡±ç‡∞§‡±ç‡∞∞‡±Ä" : lang === "hi" ? "‡§Æ‡§π‡§ø‡§≤‡§æ" : "Female"}
      </option>
      <option value="other">
        {lang === "te" ? "‡∞á‡∞§‡∞∞‡±Å‡∞≤‡±Å" : lang === "hi" ? "‡§Ö‡§®‡•ç‡§Ø" : "Other"}
      </option>
    </>
  )}

  {/* AGE */}
  {q.key === "age" && (
    <>
      <option value="below18">
        {lang === "te" ? "18 ‡∞≤‡±ã‡∞™‡±Å" : lang === "hi" ? "18 ‡§∏‡•á ‡§ï‡§Æ" : "Below 18"}
      </option>
      <option value="18-35">18 ‚Äì 35</option>
      <option value="36-59">36 ‚Äì 59</option>
      <option value="60+">
        {lang === "te" ? "60 ‡∞™‡±à‡∞¨‡∞°‡∞ø‡∞® ‡∞µ‡∞æ‡∞∞‡±Å" : lang === "hi" ? "60 ‡§∏‡•á ‡§ä‡§™‡§∞" : "60+"}
      </option>
    </>
  )}

  {/* OCCUPATION */}
  {q.key === "occupation" && (
    <>
      <option value="student">
        {lang === "te" ? "‡∞µ‡∞ø‡∞¶‡±ç‡∞Ø‡∞æ‡∞∞‡±ç‡∞•‡∞ø" : lang === "hi" ? "‡§õ‡§æ‡§§‡•ç‡§∞" : "Student"}
      </option>
      <option value="farmer">
        {lang === "te" ? "‡∞∞‡±à‡∞§‡±Å" : lang === "hi" ? "‡§ï‡§ø‡§∏‡§æ‡§®" : "Farmer"}
      </option>
      <option value="labourer">
        {lang === "te" ? "‡∞ï‡±Ç‡∞≤‡±Ä" : lang === "hi" ? "‡§Æ‡§ú‡§º‡§¶‡•Ç‡§∞" : "Labourer"}
      </option>
      <option value="elderly">
        {lang === "te" ? "‡∞µ‡±É‡∞¶‡±ç‡∞ß‡±Å‡∞°‡±Å" : lang === "hi" ? "‡§µ‡•É‡§¶‡•ç‡§ß" : "Elderly"}
      </option>
      <option value="poor">
        {lang === "te"
          ? "‡∞™‡±á‡∞¶ / ‡∞®‡∞ø‡∞∞‡±Å‡∞¶‡±ç‡∞Ø‡±ã‡∞ó‡∞ø"
          : lang === "hi"
          ? "‡§ó‡§∞‡•Ä‡§¨ / ‡§¨‡•á‡§∞‡•ã‡§ú‡§º‡§ó‡§æ‡§∞"
          : "Unemployed / Poor"}
      </option>
    </>
  )}

  {/* AREA */}
  {q.key === "area" && (
    <>
      <option value="rural">
        {lang === "te" ? "‡∞ó‡±ç‡∞∞‡∞æ‡∞Æ‡±Ä‡∞£" : lang === "hi" ? "‡§ó‡•ç‡§∞‡§æ‡§Æ‡•Ä‡§£" : "Rural"}
      </option>
      <option value="urban">
        {lang === "te" ? "‡∞®‡∞ó‡∞∞" : lang === "hi" ? "‡§∂‡§π‡§∞‡•Ä" : "Urban"}
      </option>
    </>
  )}

  {/* INCOME */}
  {q.key === "income" && (
    <>
      <option value="low">
        {lang === "te"
          ? "‚Çπ2.5 ‡∞≤‡∞ï‡±ç‡∞∑‡∞≤ ‡∞≤‡±ã‡∞™‡±Å"
          : lang === "hi"
          ? "‚Çπ2.5 ‡§≤‡§æ‡§ñ ‡§∏‡•á ‡§ï‡§Æ"
