      <option value="mid">
        {lang === "te"
          ? "‚Çπ2.5 ‚Äì 5 ‡∞≤‡∞ï‡±ç‡∞∑‡∞≤‡±Å"
          : lang === "hi"
          ? "‚Çπ2.5 ‚Äì 5 ‡§≤‡§æ‡§ñ"
          : "‚Çπ2.5 ‚Äì 5 Lakh"}
      </option>
      <option value="high">
        {lang === "te"
          ? "‚Çπ5 ‡∞≤‡∞ï‡±ç‡∞∑‡∞≤‡∞ï‡±Å ‡∞™‡±à‡∞ó‡∞æ"
          : lang === "hi"
          ? "‚Çπ5 ‡§≤‡§æ‡§ñ ‡§∏‡•á ‡§Ö‡§ß‡§ø‡§ï"
          : "Above ‚Çπ5 Lakh"}
      </option>
    </>
  )}

  {/* DISABILITY */}
  {q.key === "disability" && (
    <>
      <option value="yes">
        {lang === "te" ? "‡∞Ö‡∞µ‡±Å‡∞®‡±Å" : lang === "hi" ? "‡§π‡§æ‡§Å" : "Yes"}
      </option>
      <option value="no">
        {lang === "te" ? "‡∞ï‡∞æ‡∞¶‡±Å" : lang === "hi" ? "‡§®‡§π‡•Ä‡§Ç" : "No"}
      </option>
    </>
  )}
</select>
<button
  className="voice-btn"
  onClick={startListening}
>
  üé§ {TEXT.speak[lang]}
</button>


          <div className="nav">
            <button onClick={() => qStep === 0 ? setStep(0) : setQStep(qStep - 1)}>
              {TEXT.back[lang]}
            </button>
            <button
              className="primary"
              onClick={() =>
                qStep < QUESTIONS.length - 1 ? setQStep(qStep + 1) : setStep(2)
              }
            >
              {TEXT.next[lang]}
            </button>
