        </div>
      </div>
    );
  }
  


  /* RESULTS */
  return (
    <div className="results-page">
      <h2>{TEXT.results[lang]}</h2>

      <div className="voice-controls">
        <button onClick={startVoiceForSchemes}>üîä Start Voice</button>
        <button onClick={stopVoice}>‚èπ Stop Voice</button>
      </div>

      <div className="scheme-row">
        {eligible.map((s) => (
          <div className="scheme" key={s.id}>
            <img src={schemeImages[s.image] || defaultImg} alt={s.name[lang]} />
