            <p>{s.benefit[lang]}</p>
            <a href={s.officialLink} target="_blank" rel="noreferrer">
              More Information
            </a>
          </div>
        ))}
      </div>

      <button className="home-btn" onClick={() => {
        setStep(0);
        setQStep(0);
      }}>
        üè† Home Page
      </button>
